import axios from 'axios';
import { TransportRoute, Bus, StudentTransport } from '../models/transport';

const base = '/api/transport';

const transportService = {
  // Routes
  getRoutes: () => axios.get<TransportRoute[]>(`${base}/routes/`),
  createRoute: (data: TransportRoute) => axios.post(`${base}/routes/`, data),
  updateRoute: (id: number, data: TransportRoute) => axios.put(`${base}/routes/${id}/`, data),
  deleteRoute: (id: number) => axios.delete(`${base}/routes/${id}/`),

  // Buses
  getBuses: () => axios.get<Bus[]>(`${base}/buses/`),
  createBus: (data: Bus) => axios.post(`${base}/buses/`, data),
  updateBus: (id: number, data: Bus) => axios.put(`${base}/buses/${id}/`, data),
  deleteBus: (id: number) => axios.delete(`${base}/buses/${id}/`),

  // StudentTransport
  getStudentTransports: (params?: any) => axios.get<StudentTransport[]>(`${base}/student-transports/`, { params }),
  assignTransport: (payload: StudentTransport) => axios.post(`${base}/student-transports/`, payload),
  updateStudentTransport: (id: number, payload: StudentTransport) => axios.put(`${base}/student-transports/${id}/`, payload),
  deleteStudentTransport: (id: number) => axios.delete(`${base}/student-transports/${id}/`),
};

export default transportService;
