import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";
import { LogOut, BookOpen, Home, Wallet, User } from "lucide-react";

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const StudentDashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("home");

  const studentId = "gen002";

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await axios.get(
          `http://127.0.0.1:8000/api/academics/dashboard/${studentId}/`
        );
        setDashboardData(response.data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDashboardData();
  }, [studentId]);

  const resultChartData = useMemo(() => {
    const results = dashboardData?.results || [];
    return {
      labels: results.map((r: any) => r.course_name || "N/A"),
      datasets: [
        {
          label: "Grades %",
          data: results.map((r: any) => r.obtained_marks || 0),
          backgroundColor: "rgba(79, 70, 229, 0.7)",
          borderRadius: 6,
        },
      ],
    };
  }, [dashboardData]);

  const attendanceChartData = useMemo(() => {
    const a = dashboardData?.attendance_summary || { total_classes: 0, present_classes: 0 };
    return {
      labels: ["Total Classes", "Present Classes"],
      datasets: [
        {
          data: [a.total_classes, a.present_classes],
          backgroundColor: ["#d1d5db", "#4f46e5"],
          borderWidth: 2,
        },
      ],
    };
  }, [dashboardData]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-indigo-600">
        Loading Dashboard...
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-red-600">
        Failed to load dashboard data.
      </div>
    );
  }

  const student = dashboardData.student_info;
  const attendance = dashboardData.attendance_summary;
  const profileImage =
    student?.image && student.image.startsWith("http")
      ? student.image
      : student?.image
      ? `http://127.0.0.1:8000${student.image}`
      : null;

  const sidebarItems = [
    { key: "home", icon: <Home size={20} />, label: "Home" },
    { key: "results", icon: <BookOpen size={20} />, label: "Results" },
    { key: "fees", icon: <Wallet size={20} />, label: "Fees" },
    { key: "profile", icon: <User size={20} />, label: "Profile" },
  ];

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-indigo-50 to-white">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-xl flex flex-col justify-between border-r border-gray-100">
        <div>
          <div className="flex items-center space-x-3 p-5 border-b border-gray-200">
            {profileImage ? (
              <img
                src={profileImage}
                alt="Profile"
                className="w-12 h-12 rounded-full object-cover border-2 border-indigo-500"
              />
            ) : (
              <div className="w-12 h-12 rounded-full flex items-center justify-center bg-indigo-200 text-indigo-800 text-lg font-bold">
                {student?.name?.charAt(0)?.toUpperCase() || "S"}
              </div>
            )}
            <div>
              <p className="font-semibold text-gray-700">{student?.name}</p>
              <p className="text-sm text-gray-500">{student?.department}</p>
            </div>
          </div>

          <div className="mt-6">
            {sidebarItems.map((item) => (
              <button
                key={item.key}
                onClick={() => setActiveTab(item.key)}
                className={`flex items-center w-full px-5 py-3 text-left space-x-3 font-medium transition ${
                  activeTab === item.key
                    ? "bg-indigo-100 text-indigo-700 border-l-4 border-indigo-600"
                    : "text-gray-600 hover:bg-gray-50"
                }`}
              >
                {item.icon}
                <span>{item.label}</span>
              </button>
            ))}
          </div>
        </div>

        <button className="flex items-center space-x-2 px-5 py-3 text-red-600 hover:bg-red-50 font-semibold border-t border-gray-200">
          <LogOut size={18} />
          <span>Logout</span>
        </button>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8 overflow-y-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="bg-white rounded-2xl shadow-md p-6 mb-8 border border-gray-100"
        >
          <h1 className="text-3xl font-bold text-indigo-700 mb-2">
            Welcome back, {student?.name} 🎓
          </h1>
          <p className="text-gray-500">
            Department of {student?.department} | {student?.semester}
          </p>
        </motion.div>

        {activeTab === "home" && (
          <motion.div
            key="home"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            className="grid grid-cols-1 md:grid-cols-2 gap-8"
          >
            {/* Attendance Chart */}
            <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
              <h2 className="text-lg font-semibold text-gray-700 mb-4">📊 Attendance Summary</h2>
              <div className="flex justify-center">
                <div className="w-60">
                  <Doughnut data={attendanceChartData} />
                </div>
              </div>
              <p className="text-center mt-5 text-sm text-gray-500">
                Attendance: <span className="font-semibold">{attendance?.percentage || 0}%</span>
              </p>
            </div>

            {/* Result Chart */}
            <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100">
              <h2 className="text-lg font-semibold text-gray-700 mb-4">📘 Results Overview</h2>
              <Bar
                data={resultChartData}
                options={{
                  responsive: true,
                  plugins: { legend: { display: false } },
                  scales: {
                    y: { beginAtZero: true, grid: { color: "#f3f4f6" } },
                    x: { grid: { display: false } },
                  },
                }}
              />
            </div>
          </motion.div>
        )}

        {activeTab === "fees" && (
          <motion.div
            key="fees"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100"
          >
            <h2 className="text-lg font-semibold text-gray-700 mb-4">💰 Fees Record</h2>
            {dashboardData.fees.length === 0 ? (
              <p className="text-gray-500 text-center py-6">No fee records available yet.</p>
            ) : (
              <table className="w-full border text-sm text-center">
                <thead>
                  <tr className="bg-indigo-100 text-gray-700">
                    <th className="p-3 border">Description</th>
                    <th className="p-3 border">Amount</th>
                    <th className="p-3 border">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {dashboardData.fees.map((f: any, idx: number) => (
                    <tr key={idx} className="border-t hover:bg-indigo-50 transition text-gray-600">
                      <td className="p-3 border">{f.description}</td>
                      <td className="p-3 border font-medium">Rs. {f.amount}</td>
                      <td
                        className={`p-3 border font-semibold ${
                          f.status === "Paid" ? "text-green-600" : "text-red-600"
                        }`}
                      >
                        {f.status}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
