import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import collegeLogo from "../assets/college-logo.png";
import axios from "axios";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

interface Student {
  name: string;
  email: string;
  department: string;
  semester: string;
  roll_no: string;
  profile_image?: string;
}

const StudentDashboard: React.FC = () => {
  const [activeSection, setActiveSection] = useState("dashboard");
  const [student, setStudent] = useState<Student | null>(null);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    // Fetch student data (adjust endpoint as needed)
    axios.get("http://127.0.0.1:8000/api/students/gen002/")
      .then((res) => setStudent(res.data))
      .catch((err) => console.error("Error fetching student:", err));
  }, []);

  const resultsData = {
    labels: ["Semester 1", "Semester 2", "Semester 3", "Semester 4"],
    datasets: [
      {
        label: "GPA",
        data: [3.1, 3.4, 3.6, 3.8],
        backgroundColor: "rgba(37, 99, 235, 0.7)",
      },
    ],
  };

  return (
    <div
      className={`flex h-screen ${
        darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-800"
      } relative`}
    >
      {/* ðŸ”· Background Watermark */}
      <motion.div
        className="absolute inset-0 flex items-center justify-center pointer-events-none"
        initial={{ opacity: 0 }}
        animate={{ opacity: darkMode ? 0.03 : 0.06 }}
        transition={{ duration: 1.5 }}
      >
        <motion.img
          src={collegeLogo}
          alt="College Logo"
          className="w-80 h-80 opacity-10 select-none"
          animate={{ scale: [1, 1.03, 1], rotate: [0, 2, -2, 0] }}
          transition={{ repeat: Infinity, duration: 10, ease: "easeInOut" }}
        />
      </motion.div>

      {/* ðŸ”¹ Sidebar */}
      <div
        className={`w-64 p-6 flex flex-col justify-between ${
          darkMode ? "bg-gray-800" : "bg-white shadow-lg"
        } z-10`}
      >
        <div>
          <h2 className="text-2xl font-bold text-center mb-6 text-blue-600">
            ðŸŽ“ Student Portal
          </h2>
          <ul className="space-y-3">
            {[
              { name: "Dashboard", id: "dashboard" },
              { name: "Profile", id: "profile" },
              { name: "Results", id: "results" },
              { name: "Attendance", id: "attendance" },
              { name: "Messaging", id: "messaging" },
              { name: "Events", id: "events" },
              { name: "Announcements", id: "announcements" },
              { name: "Library", id: "library" },
            ].map((item) => (
              <li
                key={item.id}
                onClick={() => setActiveSection(item.id)}
                className={`cursor-pointer p-2 rounded-lg text-center ${
                  activeSection === item.id
                    ? "bg-blue-600 text-white"
                    : "hover:bg-blue-100 dark:hover:bg-gray-700"
                }`}
              >
                {item.name}
              </li>
            ))}
          </ul>
        </div>

        <button
          onClick={() => alert("Logged out successfully!")}
          className="mt-6 bg-red-500 text-white py-2 px-4 rounded-lg hover:bg-red-600 transition"
        >
          Logout
        </button>
      </div>

      {/* ðŸ”¹ Main Content */}
      <div className="flex-1 p-10 overflow-y-auto relative z-10">
        {/* Toggle Dark Mode */}
        <div className="absolute top-4 right-4">
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="bg-blue-600 text-white px-3 py-1 rounded-md"
          >
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </div>

        {/* Section Content */}
        {activeSection === "dashboard" && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <h1 className="text-3xl font-bold mb-4">Welcome, {student?.name}</h1>
            <p className="text-gray-500">
              Explore your dashboard to see your profile, results, and updates.
            </p>

            <div className="mt-10 bg-white dark:bg-gray-800 p-6 rounded-2xl shadow-xl">
              <h2 className="text-xl font-semibold mb-4 text-blue-600">Academic Performance</h2>
              <Bar data={resultsData} />
            </div>
          </motion.div>
        )}

        {activeSection === "profile" && student && (
          <motion.div
            className="max-w-xl mx-auto bg-white dark:bg-gray-800 p-8 rounded-2xl shadow-lg"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
          >
            <div className="flex flex-col items-center">
              <img
                src={student.profile_image || "https://via.placeholder.com/100"}
                alt="Profile"
                className="w-28 h-28 rounded-full border-4 border-blue-500 mb-4 object-cover"
              />
              <h2 className="text-2xl font-semibold text-blue-600 mb-2">{student.name}</h2>
              <p className="text-gray-600 dark:text-gray-300">{student.email}</p>
              <div className="mt-6 text-left w-full space-y-2">
                <p><strong>Department:</strong> {student.department}</p>
                <p><strong>Semester:</strong> {student.semester}</p>
                <p><strong>Roll No:</strong> {student.roll_no}</p>
              </div>
            </div>
          </motion.div>
        )}

        {activeSection === "results" && (
          <div className="p-8 bg-white dark:bg-gray-800 rounded-xl shadow-lg">
            <h2 className="text-xl font-semibold text-blue-600 mb-4">Results Overview</h2>
            <Bar data={resultsData} />
          </div>
        )}

        {/* Placeholder for other sections */}
        {["attendance", "messaging", "events", "announcements", "library"].includes(activeSection) && (
          <div className="text-center text-gray-500 mt-20">
            <p>ðŸ“¢ {activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} section coming soon...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
