import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Bar } from "react-chartjs-2";
import {
  BookOpen,
  User,
  FileText,
  LogOut,
  Camera,
  Bell,
} from "lucide-react";
import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:8000/api";

const StudentDashboard: React.FC = () => {
  const [studentData, setStudentData] = useState<any>(null);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [uploading, setUploading] = useState<boolean>(false);
  const [dashboardData, setDashboardData] = useState({
    attendance: 85,
    assignments: 12,
    grade: "A",
  });

  const studentId = "gen002"; // Example â€“ dynamically from auth later

  useEffect(() => {
    fetchStudentData();
  }, []);

  const fetchStudentData = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/students/${studentId}/`);
      setStudentData(response.data);
    } catch (error) {
      console.error("Error fetching student data:", error);
    }
  };

  const handleImageUpload = async () => {
    if (!selectedImage) return alert("Please select an image first!");
    setUploading(true);

    const formData = new FormData();
    formData.append("image", selectedImage);

    try {
      const response = await axios.patch(
       ` ${API_BASE_URL}/students/${studentId}/upload-image/`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setStudentData({ ...studentData, image: response.data.image });
      alert("Profile image updated successfully!");
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Error uploading image!");
    } finally {
      setUploading(false);
    }
  };

  const chartData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
    datasets: [
      {
        label: "Attendance %",
        data: [85, 90, 88, 92],
        backgroundColor: "rgba(59, 130, 246, 0.6)",
      },
    ],
  };

  return (
    <div className="flex min-h-screen bg-gray-50 text-gray-800">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-xl flex flex-col justify-between">
        <div>
          <div className="p-6 flex items-center gap-3 border-b">
            <User className="text-blue-500" />
            <h2 className="font-bold text-lg">Student Dashboard</h2>
          </div>

          <nav className="p-4 flex flex-col gap-4">
            <button className="flex items-center gap-3 hover:text-blue-600">
              <BookOpen size={18} /> Library
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <FileText size={18} /> Assignments
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <Bell size={18} /> Notifications
            </button>
          </nav>
        </div>

        <div className="p-4 border-t flex items-center gap-2 hover:text-red-600 cursor-pointer">
          <LogOut size={18} /> Logout
        </div>
      </div>

      {/* Main Dashboard */}
      <div className="flex-1 p-8">
        <motion.h1
          className="text-2xl font-bold mb-6 text-blue-600"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          Welcome Back, {studentData?.name || "Student"} ðŸ‘‹
        </motion.h1>

        {/* Profile Section */}
        <motion.div
          className="bg-white rounded-2xl shadow-md p-6 flex items-center gap-6 mb-6"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          <div className="relative">
            <img
              src={
                studentData?.image
                  ? `${studentData.image}`
                  : "https://via.placeholder.com/100"
              }
              alt="Profile"
              className="w-24 h-24 rounded-full object-cover border"
            />
            <label className="absolute bottom-0 right-0 bg-blue-500 p-2 rounded-full cursor-pointer">
              <Camera size={18} color="white" />
              <input
                type="file"
                hidden
                onChange={(e) =>
                  setSelectedImage(e.target.files ? e.target.files[0] : null)
                }
              />
            </label>
          </div>

          <div>
            <h2 className="font-semibold text-lg">{studentData?.name}</h2>
            <p className="text-gray-600">{studentData?.email}</p>
            <button
              onClick={handleImageUpload}
              disabled={uploading}
              className="mt-3 bg-blue-600 text-white px-4 py-1 rounded-lg hover:bg-blue-700"
            >
              {uploading ? "Uploading..." : "Upload Image"}
            </button>
          </div>
        </motion.div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <motion.div
            whileHover={{ scale: 1.05 }}
            className="bg-blue-100 p-6 rounded-xl shadow-sm text-center"
          >
            <h3 className="font-semibold text-lg">Attendance</h3>
            <p className="text-3xl font-bold text-blue-600">
              {dashboardData.attendance}%
            </p>
          </motion.div>

          <motion.div
            whileHover={{ scale: 1.05 }}
            className="bg-green-100 p-6 rounded-xl shadow-sm text-center"
          >
            <h3 className="font-semibold text-lg">Assignments</h3>
            <p className="text-3xl font-bold text-green-600">
              {dashboardData.assignments}
            </p>
          </motion.div>

          <motion.div
            whileHover={{ scale: 1.05 }}
            className="bg-yellow-100 p-6 rounded-xl shadow-sm text-center"
          >
            <h3 className="font-semibold text-lg">Grade</h3>
            <p className="text-3xl font-bold text-yellow-600">
              {dashboardData.grade}
            </p>
          </motion.div>
        </div>

        {/* Chart Section */}
        <div className="bg-white p-6 rounded-2xl shadow-md">
          <h3 className="text-lg font-semibold mb-4 text-gray-700">
            Weekly Attendance Overview
          </h3>
          <Bar data={chartData} />
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
