import React, { useState, useEffect } from 'react';
import { academicsService } from '../api/academicsService';

interface Fee {
  fee_id: number;
  student: number;
  term: string;
  amount: string;
  paid_amount: string;
  status: 'Paid' | 'Unpaid' | 'Partial';
  due_date: string;
  paid_on?: string;
  balance: number;
  department_id: number;
  semester_id: number;
}

interface Student {
  id: number;
  first_name: string;
  last_name: string;
  registration_number: string;
  department_id: number;
  semester_id: number;
}

interface Department {
  id: number;
  name: string;
}

interface Semester {
  id: number;
  name: string;
  semester_code: string;
}

const FeeManagement: React.FC = () => {
  const [fees, setFees] = useState<Fee[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [semesters, setSemesters] = useState<Semester[]>([]);
  const [filteredFees, setFilteredFees] = useState<Fee[]>([]);
  const [selectedStudent, setSelectedStudent] = useState<number | null>(null);
  const [selectedDepartment, setSelectedDepartment] = useState<number | null>(null);
  const [selectedSemester, setSelectedSemester] = useState<number | null>(null);
  const [statusFilter, setStatusFilter] = useState<string>('All');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedFee, setSelectedFee] = useState<Fee | null>(null);
  const [paymentAmount, setPaymentAmount] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fee amounts based on semester type
  const FEE_AMOUNTS = {
    even: 96000, // 96k for even semesters
    odd: 84000   // 84k for odd semesters
  };

  useEffect(() => {
    fetchDepartments();
    fetchStudents();
    fetchSemesters();
  }, []);

  useEffect(() => {
    if (selectedDepartment && selectedSemester) {
      generateFeesForStudents();
    }
  }, [selectedDepartment, selectedSemester, students]);

  useEffect(() => {
    filterFees();
  }, [fees, selectedStudent, statusFilter]);

  const fetchDepartments = async () => {
    try {
      const response = await academicsService.getDepartments();
      setDepartments(response.data);
    } catch (err) {
      console.error('Failed to fetch departments:', err);
    }
  };

  const fetchSemesters = async () => {
    try {
      const response = await academicsService.getSemesters();
      setSemesters(response.data);
    } catch (err) {
      console.error('Failed to fetch semesters:', err);
    }
  };

  const fetchStudents = async () => {
    try {
      const response = await academicsService.getStudents();
      setStudents(response.data);
    } catch (err) {
      console.error('Failed to fetch students:', err);
    }
  };

  const isEvenSemester = (semesterCode: string): boolean => {
    const semesterNumber = parseInt(semesterCode.replace(/\D/g, ''));
    return semesterNumber % 2 === 0;
  };

  const calculateFeeAmount = (semesterCode: string): number => {
    return isEvenSemester(semesterCode) ? FEE_AMOUNTS.even : FEE_AMOUNTS.odd;
  };

  const generateFeesForStudents = () => {
    if (!selectedDepartment || !selectedSemester) return;

    const selectedSem = semesters.find(s => s.id === selectedSemester);
    if (!selectedSem) return;

    const feeAmount = calculateFeeAmount(selectedSem.semester_code);
    const currentDate = new Date();
    const dueDate = new Date(currentDate.setMonth(currentDate.getMonth() + 1)).toISOString().split('T')[0];

    const newFees: Fee[] = students
      .filter(student => student.department_id === selectedDepartment)
      .map((student, index) => ({
        fee_id: Date.now() + index,
        student: student.id,
        term: `${selectedSem.name} - ${selectedSem.semester_code}`,
        amount: feeAmount.toString(),
        paid_amount: '0',
        status: 'Unpaid' as const,
        due_date: dueDate,
        balance: feeAmount,
        department_id: selectedDepartment,
        semester_id: selectedSemester
      }));

    setFees(newFees);
  };

  const filterFees = () => {
    let filtered = fees;
    if (selectedStudent) {
      filtered = filtered.filter(fee => fee.student === selectedStudent);
    }
    if (statusFilter !== 'All') {
      filtered = filtered.filter(fee => fee.status === statusFilter);
    }
    setFilteredFees(filtered);
  };

  const handlePayment = async () => {
    if (!selectedFee || !paymentAmount) return;

    setIsLoading(true);
    try {
      const updatedPaidAmount = parseFloat(selectedFee.paid_amount) + parseFloat(paymentAmount);
      const newStatus = updatedPaidAmount >= parseFloat(selectedFee.amount) ? 'Paid' : 'Partial';

      const updatedFee: Fee = {
        ...selectedFee,
        paid_amount: updatedPaidAmount.toString(),
        status: newStatus as 'Paid' | 'Unpaid' | 'Partial',
        paid_on: new Date().toISOString().split('T')[0],
      };

      await academicsService.updateFee(selectedFee.fee_id, updatedFee);
      setFees(fees.map(fee => fee.fee_id === selectedFee.fee_id ? updatedFee as Fee : fee));
      setIsModalOpen(false);
      setSelectedFee(null);
      setPaymentAmount('');
    } catch (err) {
      setError('Failed to update payment');
    } finally {
      setIsLoading(false);
    }
  };

  const openPaymentModal = (fee: Fee) => {
    setSelectedFee(fee);
    setPaymentAmount('');
    setIsModalOpen(true);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Fee Management</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="mb-4 flex space-x-4">
        <div>
          <label className="block text-gray-700 text-sm font-bold mb-2">Filter by Student</label>
          <select
            value={selectedStudent || ''}
            onChange={(e) => setSelectedStudent(e.target.value ? parseInt(e.target.value) : null)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="">All Students</option>
            {students.map(student => (
              <option key={student.id} value={student.id}>
                {student.first_name} {student.last_name} ({student.registration_number})
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-gray-700 text-sm font-bold mb-2">Filter by Status</label>
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="All">All</option>
            <option value="Paid">Paid</option>
            <option value="Unpaid">Unpaid</option>
            <option value="Partial">Partial</option>
          </select>
        </div>
      </div>

      {isLoading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
        </div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="py-2 px-4 border-b text-left">Term</th>
                <th className="py-2 px-4 border-b text-left">Amount</th>
                <th className="py-2 px-4 border-b text-left">Paid Amount</th>
                <th className="py-2 px-4 border-b text-left">Balance</th>
                <th className="py-2 px-4 border-b text-left">Status</th>
                <th className="py-2 px-4 border-b text-left">Due Date</th>
                <th className="py-2 px-4 border-b text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredFees.map(fee => (
                <tr key={fee.fee_id} className="hover:bg-gray-50">
                  <td className="py-2 px-4 border-b">{fee.term}</td>
                  <td className="py-2 px-4 border-b">${fee.amount}</td>
                  <td className="py-2 px-4 border-b">${fee.paid_amount}</td>
                  <td className="py-2 px-4 border-b">${fee.balance.toFixed(2)}</td>
                  <td className="py-2 px-4 border-b">
                    <span className={`px-2 py-1 rounded text-xs font-semibold ${
                      fee.status === 'Paid' ? 'bg-green-100 text-green-800' :
                      fee.status === 'Unpaid' ? 'bg-red-100 text-red-800' :
                      'bg-yellow-100 text-yellow-800'
                    }`}>
                      {fee.status}
                    </span>
                  </td>
                  <td className="py-2 px-4 border-b">{fee.due_date}</td>
                  <td className="py-2 px-4 border-b">
                    <button
                      onClick={() => openPaymentModal(fee)}
                      className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-1 px-3 rounded text-sm"
                    >
                      Make Payment
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {isModalOpen && selectedFee && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-md">
            <div className="p-6">
              <h2 className="text-xl font-bold mb-4">Make Payment</h2>
              <p className="mb-4">Fee for {selectedFee.term}</p>
              <p className="mb-4">Total Amount: ${selectedFee.amount}</p>
              <p className="mb-4">Balance: ${selectedFee.balance.toFixed(2)}</p>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">Payment Amount</label>
                <input
                  type="number"
                  value={paymentAmount}
                  onChange={(e) => setPaymentAmount(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  placeholder="Enter amount"
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setIsModalOpen(false)}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
                >
                  Cancel
                </button>
                <button
                  onClick={handlePayment}
                  disabled={isLoading || !paymentAmount}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded"
                >
                  {isLoading ? 'Processing...' : 'Pay'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FeeManagement;
