import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Bar, Line } from "react-chartjs-2";
import {
  GraduationCap,
  ClipboardList,
  CalendarDays,
  MessageSquare,
  Bell,
  BookOpen,
  User,
  LogOut,
  Moon,
  Sun,
  Camera,
} from "lucide-react";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);

const API_BASE_URL = "http://127.0.0.1:8000/api";

const StudentDashboard: React.FC = () => {
  const [studentData, setStudentData] = useState<any>(null);
  const [announcements, setAnnouncements] = useState<any[]>([]);
  const [results, setResults] = useState<any[]>([]);
  const [attendance, setAttendance] = useState<number[]>([85, 90, 88, 92]);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [activeTab, setActiveTab] = useState("Dashboard");

  const studentId = "gen002";

  useEffect(() => {
    fetchStudentData();
    fetchAnnouncements();
    fetchResults();
  }, []);

  const fetchStudentData = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/students/${studentId}/`);
      setStudentData(response.data);
    } catch (error) {
      console.error("Error fetching student data:", error);
    }
  };

  const fetchAnnouncements = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/announcements/`);
      setAnnouncements(response.data);
    } catch (error) {
      console.error("Error fetching announcements:", error);
    }
  };

  const fetchResults = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/results/?student_id=${studentId}`);
      setResults(response.data);
    } catch (error) {
      console.error("Error fetching results:", error);
    }
  };

  const handleImageUpload = async () => {
    if (!selectedImage) return alert("Please select an image!");
    setUploading(true);
    const formData = new FormData();
    formData.append("image", selectedImage);
    try {
      const response = await axios.patch(
        `${API_BASE_URL}/students/${studentId}/upload-image/`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setStudentData({ ...studentData, image: response.data.image });
      alert("Profile image updated!");
    } catch (error) {
      console.error("Upload failed:", error);
    } finally {
      setUploading(false);
    }
  };

  // ----- Charts -----
  const attendanceChartData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
    datasets: [
      {
        label: "Attendance %",
        data: attendance,
        backgroundColor: darkMode ? "rgba(147,197,253,0.7)" : "rgba(37,99,235,0.6)",
        borderRadius: 10,
      },
    ],
  };

  const resultChartData = {
    labels: results.map((r) => r.subject_name || "Subject"),
    datasets: [
      {
        label: "GPA",
        data: results.map((r) => r.gpa || 0),
        fill: false,
        borderColor: darkMode ? "#60a5fa" : "#2563eb",
        tension: 0.3,
      },
    ],
  };

  // ----- Sidebar Menu -----
  const menuItems = [
    { name: "Dashboard", icon: <ClipboardList size={18} /> },
    { name: "Results", icon: <GraduationCap size={18} /> },
    { name: "Attendance", icon: <CalendarDays size={18} /> },
    { name: "Announcements", icon: <Bell size={18} /> },
    { name: "Messages", icon: <MessageSquare size={18} /> },
    { name: "Library", icon: <BookOpen size={18} /> },
    { name: "Profile", icon: <User size={18} /> },
  ];

  return (
    <div
      className={`flex min-h-screen transition-all duration-500 ${
        darkMode ? "bg-gray-900 text-gray-100" : "bg-gray-100 text-gray-800"
      }`}
    >
      {/* Sidebar */}
      <div
        className={`w-72 ${
          darkMode ? "bg-gray-800" : "bg-white"
        } shadow-xl flex flex-col justify-between`}
      >
        <div>
          {/* Header */}
          <div className="p-6 flex items-center justify-between border-b border-gray-200">
            <div className="flex items-center gap-2">
              <GraduationCap className="text-blue-600" size={26} />
              <h2 className="font-bold text-lg tracking-tight">Student Portal</h2>
            </div>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
            >
              {darkMode ? <Sun size={18} /> : <Moon size={18} />}
            </button>
          </div>

          {/* Menu */}
          <nav className="p-5 flex flex-col gap-3 font-medium">
            {menuItems.map((item) => (
              <button
                key={item.name}
                onClick={() => setActiveTab(item.name)}
                className={`flex items-center gap-3 px-3 py-2 rounded-md transition ${
                  activeTab === item.name
                    ? "bg-blue-100 text-blue-600"
                    : "hover:text-blue-500"
                }`}
              >
                {item.icon} {item.name}
              </button>
            ))}
          </nav>

          {/* Sidebar Profile */}
          {studentData && (
            <div
              className={`mt-6 mx-4 p-4 rounded-xl ${
                darkMode ? "bg-gray-700" : "bg-gray-50"
              }`}
            >
              <div className="flex items-center gap-3 mb-3">
                <img
                  src={
                    studentData?.image
                      ? `${studentData.image}`
                      : "https://via.placeholder.com/80"
                  }
                  alt="Profile"
                  className="w-12 h-12 rounded-full object-cover border-2 border-blue-500"
                />
                <div>
                  <h3 className="font-semibold text-sm">{studentData.name}</h3>
                  <p className="text-xs text-gray-500">{studentData.student_id}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Logout */}
        <div className="p-5 border-t flex items-center gap-2 hover:text-red-500 cursor-pointer">
          <LogOut size={18} /> Logout
        </div>
      </div>

      {/* Main Section */}
      <div className="flex-1 p-8 overflow-y-auto">
        {activeTab === "Dashboard" && (
          <>
            <h1 className="text-3xl font-bold text-blue-600 mb-6">
              Welcome, {studentData?.name || "Student"} 👋
            </h1>
            <div className="grid md:grid-cols-2 gap-8">
              <motion.div
                className={`rounded-2xl shadow-md p-6 ${
                  darkMode ? "bg-gray-800" : "bg-white"
                }`}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <h3 className="text-lg font-semibold mb-4">Attendance Overview</h3>
                <Bar data={attendanceChartData} />
              </motion.div>

              <motion.div
                className={`rounded-2xl shadow-md p-6 ${
                  darkMode ? "bg-gray-800" : "bg-white"
                }`}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <h3 className="text-lg font-semibold mb-4">GPA Progress</h3>
                <Line data={resultChartData} />
              </motion.div>
            </div>
          </>
        )}

        {activeTab === "Announcements" && (
          <motion.div
            className={`rounded-2xl shadow-md p-8 ${
              darkMode ? "bg-gray-800" : "bg-white"
            }`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <h3 className="text-xl font-semibold mb-4">Latest Announcements</h3>
            {announcements.length ? (
              announcements.map((a) => (
                <div key={a.id} className="mb-3 border-b pb-2">
                  <h4 className="font-semibold">{a.title}</h4>
                  <p className="text-sm text-gray-500">{a.description}</p>
                </div>
              ))
            ) : (
              <p>No announcements yet.</p>
            )}
          </motion.div>
        )}

        {activeTab === "Profile" && studentData && (
          <motion.div
            className={`rounded-2xl shadow-md p-8 ${
              darkMode ? "bg-gray-800" : "bg-white"
            }`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <div className="flex flex-col items-center gap-4">
              <div className="relative">
                <img
                  src={
                    studentData?.image
                      ? `${studentData.image}`
                      : "https://via.placeholder.com/150"
                  }
                  alt="Profile"
                  className="w-32 h-32 rounded-full object-cover border-4 border-blue-500"
                />
                <label className="absolute bottom-1 right-1 bg-blue-500 text-white p-2 rounded-full cursor-pointer hover:bg-blue-600">
                  <Camera size={16} />
                  <input
                    type="file"
                    className="hidden"
                    onChange={(e) => setSelectedImage(e.target.files?.[0] || null)}
                  />
                </label>
              </div>
              <button
                onClick={handleImageUpload}
                disabled={uploading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                {uploading ? "Uploading..." : "Update Image"}
              </button>

              <div className="mt-6 grid grid-cols-2 gap-4 text-gray-700 dark:text-gray-300">
                <p><strong>Name:</strong> {studentData.name}</p>
                <p><strong>ID:</strong> {studentData.student_id}</p>
                <p><strong>Email:</strong> {studentData.email}</p>
                <p><strong>Phone:</strong> {studentData.phone}</p>
                <p><strong>Department:</strong> {studentData.department?.name}</p>
                <p><strong>Semester:</strong> {studentData.semester?.name}</p>
              </div>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
