import React, { useState } from "react";
import { libraryService } from "../../api/libraryService";

type Props = { isOpen: boolean; onClose: () => void; onSaved: () => void; initial?: any };

const AddBookModal: React.FC<Props> = ({ isOpen, onClose, onSaved, initial }) => {
  const [title, setTitle] = useState(initial?.title || "");
  const [author, setAuthor] = useState(initial?.author || "");
  const [isbn, setIsbn] = useState(initial?.isbn || "");
  const [category, setCategory] = useState(initial?.category || "");
  const [copies, setCopies] = useState(initial?.available_copies ?? 1);
  const [saving, setSaving] = useState(false);

  if (!isOpen) return null;
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSaving(true);
    try {
      const payload = { title, author, isbn, category, available_copies: copies };
      if (initial?.id) await libraryService.updateBook(initial.id, payload);
      else await libraryService.createBook(payload);
      onSaved();
      onClose();
    } catch (err) {
      console.error(err);
      alert("Save failed");
    } finally { setSaving(false); }
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center">
      <form onSubmit={handleSubmit} className="bg-white p-4 rounded w-96">
        <h3 className="text-lg font-semibold mb-2">{initial?.id ? "Edit Book" : "Add Book"}</h3>
        <input required value={title} onChange={e=>setTitle(e.target.value)} placeholder="Title" className="w-full mb-2 p-2 border" />
        <input required value={author} onChange={e=>setAuthor(e.target.value)} placeholder="Author" className="w-full mb-2 p-2 border" />
        <input required value={isbn} onChange={e=>setIsbn(e.target.value)} placeholder="ISBN" className="w-full mb-2 p-2 border" />
        <input value={category} onChange={e=>setCategory(e.target.value)} placeholder="Category" className="w-full mb-2 p-2 border" />
        <input type="number" min={0} value={copies} onChange={e=>setCopies(Number(e.target.value))} className="w-full mb-2 p-2 border" />
        <div className="flex justify-end gap-2">
          <button type="button" onClick={onClose} className="px-3 py-1 border rounded">Cancel</button>
          <button type="submit" disabled={saving} className="px-3 py-1 bg-blue-600 text-white rounded">{saving ? "Saving..." : "Save"}</button>
        </div>
      </form>
    </div>
  );
};

export default AddBookModal;
