import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Bar } from "react-chartjs-2";
import {
  GraduationCap,
  ClipboardList,
  MessageSquare,
  CalendarDays,
  Bell,
  BookOpen,
  LogOut,
  Camera,
  Moon,
  Sun,
  Mail,
  Phone,
  Layers,
  User,
} from "lucide-react";
import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:8000/api";

const StudentDashboard: React.FC = () => {
  const [studentData, setStudentData] = useState<any>(null);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [aiFeedback, setAiFeedback] = useState("");
  const [activeTab, setActiveTab] = useState("Dashboard");
  const studentId = "gen002";

  useEffect(() => {
    fetchStudentData();
  }, []);

  const fetchStudentData = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/students/${studentId}/`);
      setStudentData(response.data);
      generateAIFeedback(response.data);
    } catch (error) {
      console.error("Error fetching student data:", error);
    }
  };

  const generateAIFeedback = (data: any) => {
    const gpa = data?.gpa || 0;
    const attendance = 90;
    if (gpa > 3.5 && attendance > 85)
      setAiFeedback("ðŸŒŸ Excellent performance! Keep it up!");
    else if (gpa > 3.0)
      setAiFeedback("ðŸ‘ Great job! A little more consistency will make you shine.");
    else if (gpa > 2.5)
      setAiFeedback("ðŸ“ˆ Youâ€™re improving, focus on weak subjects.");
    else setAiFeedback("ðŸš€ You can do much better! Focus on attendance & revision.");
  };

  const handleImageUpload = async () => {
    if (!selectedImage) return alert("Please select an image!");
    setUploading(true);
    const formData = new FormData();
    formData.append("image", selectedImage);
    try {
      const response = await axios.patch(
        `${API_BASE_URL}/students/${studentId}/upload-image/`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setStudentData({ ...studentData, image: response.data.image });
      alert("Profile image updated!");
    } catch (error) {
      console.error("Upload failed:", error);
    } finally {
      setUploading(false);
    }
  };

  const chartData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
    datasets: [
      {
        label: "Attendance %",
        data: [85, 90, 88, 92],
        backgroundColor: darkMode
          ? "rgba(147,197,253,0.7)"
          : "rgba(37,99,235,0.6)",
      },
    ],
  };

  const menuItems = [
    { name: "Dashboard", icon: <Layers size={18} /> },
    { name: "Results", icon: <ClipboardList size={18} /> },
    { name: "Attendance", icon: <CalendarDays size={18} /> },
    { name: "Messaging", icon: <MessageSquare size={18} /> },
    { name: "Events", icon: <CalendarDays size={18} /> },
    { name: "Announcements", icon: <Bell size={18} /> },
    { name: "Library", icon: <BookOpen size={18} /> },
    { name: "Profile", icon: <User size={18} /> },
  ];

  return (
    <div
      className={`flex min-h-screen transition-all duration-500 ${
        darkMode ? "bg-gray-900 text-gray-100" : "bg-gray-100 text-gray-800"
      }`}
    >
      {/* Sidebar */}
      <div
        className={`w-72 ${
          darkMode ? "bg-gray-800" : "bg-white"
        } shadow-xl flex flex-col justify-between`}
      >
        <div>
          {/* Header */}
          <div className="p-6 flex items-center justify-between border-b border-gray-200">
            <div className="flex items-center gap-2">
              <GraduationCap className="text-blue-600" size={26} />
              <h2 className="font-bold text-lg tracking-tight">Student Portal</h2>
            </div>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
            >
              {darkMode ? <Sun size={18} /> : <Moon size={18} />}
            </button>
          </div>

          {/* Menu */}
          <nav className="p-5 flex flex-col gap-3 font-medium">
            {menuItems.map((item) => (
              <button
                key={item.name}
                onClick={() => setActiveTab(item.name)}
                className={`flex items-center gap-3 px-3 py-2 rounded-md transition ${
                  activeTab === item.name
                    ? "bg-blue-100 text-blue-600"
                    : "hover:text-blue-500"
                }`}
              >
                {item.icon} {item.name}
              </button>
            ))}
          </nav>

          {/* Sidebar Profile Summary */}
          {studentData && (
            <div
              className={`mt-8 mx-4 p-4 rounded-xl ${
                darkMode ? "bg-gray-700" : "bg-gray-50"
              }`}
            >
              <div className="flex items-center gap-3 mb-3">
                <img
                  src={
                    studentData?.image
                      ? `${studentData.image}`
                      : "https://via.placeholder.com/80"
                  }
                  alt="Profile"
                  className="w-12 h-12 rounded-full object-cover border-2 border-blue-500"
                />
                <div>
                  <h3 className="font-semibold text-sm">
                    {studentData.name || "Student"}
                  </h3>
                  <p className="text-xs text-gray-500">{studentData.student_id}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Logout */}
        <div className="p-5 border-t flex items-center gap-2 hover:text-red-500 cursor-pointer">
          <LogOut size={18} /> Logout
        </div>
      </div>

      {/* Main Area */}
      <div className="flex-1 p-8">
        {activeTab === "Dashboard" && (
          <>
            <motion.div
              className="mb-8"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
            >
              <h1 className="text-3xl font-bold text-blue-600 mb-2">
                Welcome, {studentData?.name || "Student"} ðŸ‘‹
              </h1>
              <p className="text-gray-500">
                {new Date().toLocaleDateString("en-GB", {
                  weekday: "long",
                  day: "numeric",
                  month: "short",
                  year: "numeric",
                })}
              </p>
            </motion.div>

            <div className="grid md:grid-cols-2 gap-8">
              <motion.div
                className={`rounded-2xl shadow-md p-6 ${
                  darkMode ? "bg-gray-800" : "bg-white"
                }`}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <h3 className="text-lg font-semibold mb-4">
                  Weekly Attendance Overview
                </h3>
                <Bar data={chartData} />
              </motion.div>

              <motion.div
                className={`rounded-2xl shadow-md p-6 ${
                  darkMode ? "bg-gray-800" : "bg-white"
                }`}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <h3 className="text-lg font-semibold mb-3">
                  AI Performance Insight ðŸ¤–
                </h3>
                <p className="text-sm leading-relaxed text-gray-500 dark:text-gray-300">
                  {aiFeedback}
                </p>
              </motion.div>
            </div>
          </>
        )}

        {activeTab === "Profile" && studentData && (
          <motion.div
            className={`rounded-2xl shadow-md p-8 ${
              darkMode ? "bg-gray-800" : "bg-white"
            }`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <div className="flex flex-col items-center gap-4">
              <div className="relative">
                <img
                  src={
                    studentData?.image
                      ? `${studentData.image}`
                      : "https://via.placeholder.com/150"
                  }
                  alt="Profile"
                  className="w-32 h-32 rounded-full object-cover border-4 border-blue-500"
                />
                <label className="absolute bottom-1 right-1 bg-blue-500 text-white p-2 rounded-full cursor-pointer hover:bg-blue-600">
                  <Camera size={16} />
                  <input
                    type="file"
                    className="hidden"
                    onChange={(e) =>
                      setSelectedImage(e.target.files?.[0] || null)
                    }
                  />
                </label>
              </div>
              <button
                onClick={handleImageUpload}
                disabled={uploading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                {uploading ? "Uploading..." : "Update Image"}
              </button>

              <div className="mt-6 grid grid-cols-2 gap-4 text-gray-700 dark:text-gray-300">
                <p><strong>Name:</strong> {studentData.name}</p>
                <p><strong>ID:</strong> {studentData.student_id}</p>
                <p><strong>Email:</strong> {studentData.email}</p>
                <p><strong>Phone:</strong> {studentData.phone}</p>
                <p><strong>Department:</strong> {studentData.department?.name}</p>
                <p><strong>Semester:</strong> {studentData.semester?.name}</p>
              </div>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
