import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";
import { User, Mail, GraduationCap, BookOpen, Award, DollarSign } from "lucide-react";

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const StudentDashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  const studentId = "gen002"; // TODO: Replace later with logged-in student ID

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await axios.get(
          `http://127.0.0.1:8000/api/academics/dashboard/${studentId}/`
        );
        setDashboardData(response.data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [studentId]);

  // âœ… Charts
  const resultChartData = useMemo(() => {
    const results = dashboardData?.results || [];
    return {
      labels: results.map((r: any) => r.course_name || "N/A"),
      datasets: [
        {
          label: "Grades (%)",
          data: results.map((r: any) => r.obtained_marks || 0),
          backgroundColor: "rgba(79, 70, 229, 0.6)",
          borderRadius: 8,
        },
      ],
    };
  }, [dashboardData]);

  const attendanceChartData = useMemo(() => {
    const a = dashboardData?.attendance_summary || { total_classes: 0, present_classes: 0 };
    return {
      labels: ["Total Classes", "Present Classes"],
      datasets: [
        {
          label: "Attendance",
          data: [a.total_classes, a.present_classes],
          backgroundColor: ["rgba(59,130,246,0.7)", "rgba(16,185,129,0.7)"],
          borderWidth: 1,
        },
      ],
    };
  }, [dashboardData]);

  if (loading)
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold">
        Loading Dashboard...
      </div>
    );

  if (!dashboardData)
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-red-600">
        Failed to load dashboard data.
      </div>
    );

  const student = dashboardData.student_info;
  const attendance = dashboardData.attendance_summary;

  return (
    <div className="p-8 space-y-8 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex flex-col md:flex-row items-center justify-between bg-white p-6 rounded-2xl shadow">
        <div>
          <h1 className="text-3xl font-bold text-indigo-700 flex items-center gap-2">
            <GraduationCap className="w-8 h-8" /> Student Dashboard
          </h1>
          <p className="text-gray-600 mt-2">Welcome back, {student?.name || "Student"} ðŸŽ‰</p>
        </div>
        {student?.image && (
          <img
            src={`http://127.0.0.1:8000${student.image}`}
            alt="Profile"
            className="w-24 h-24 rounded-full object-cover border-4 border-indigo-500 shadow-md mt-4 md:mt-0"
          />
        )}
      </div>

      {/* Info Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {[
          { label: "Name", value: student?.name, icon: <User /> },
          { label: "Email", value: student?.email, icon: <Mail /> },
          { label: "Department", value: student?.department, icon: <BookOpen /> },
          { label: "Semester", value: student?.semester, icon: <BookOpen /> },
          { label: "GPA", value: student?.gpa, icon: <Award /> },
          { label: "CGPA", value: student?.cgpa, icon: <Award /> },
        ].map((info, idx) => (
          <div
            key={idx}
            className="flex items-center gap-3 bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600 hover:shadow-md transition-all"
          >
            <div className="text-indigo-600">{info.icon}</div>
            <div>
              <h2 className="font-semibold text-gray-700">{info.label}</h2>
              <p className="text-gray-500 text-sm">{info.value || "N/A"}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="bg-white p-6 shadow rounded-2xl hover:shadow-md transition">
          <h2 className="text-lg font-bold text-gray-700 mb-4 flex items-center gap-2">
            <BookOpen className="text-indigo-600" /> Attendance Summary
          </h2>
          <Doughnut data={attendanceChartData} />
          <p className="text-center mt-4 text-sm text-gray-600">
            Attendance: <strong>{attendance?.percentage || 0}%</strong>
          </p>
        </div>

        <div className="bg-white p-6 shadow rounded-2xl hover:shadow-md transition">
          <h2 className="text-lg font-bold text-gray-700 mb-4 flex items-center gap-2">
            <Award className="text-indigo-600" /> Results Overview
          </h2>
          <Bar data={resultChartData} />
        </div>
      </div>

      {/* Fees Section */}
      <div className="bg-white p-6 shadow rounded-2xl mt-8 hover:shadow-md transition">
        <h2 className="text-lg font-bold text-gray-700 mb-4 flex items-center gap-2">
          <DollarSign className="text-indigo-600" /> Fees Record
        </h2>
        {dashboardData.fees.length === 0 ? (
          <p className="text-gray-500 text-sm">No fee records available yet.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full border text-sm rounded-xl overflow-hidden">
              <thead>
                <tr className="bg-indigo-100 text-gray-700">
                  <th className="p-3 border">Description</th>
                  <th className="p-3 border">Amount</th>
                  <th className="p-3 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {dashboardData.fees.map((f: any, idx: number) => (
                  <tr key={idx} className="text-center hover:bg-indigo-50">
                    <td className="p-2 border">{f.description}</td>
                    <td className="p-2 border">Rs. {f.amount}</td>
                    <td
                      className={`p-2 border font-medium ${
                        f.status === "Paid" ? "text-green-600" : "text-red-600"
                      }`}
                    >
                      {f.status}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
