import axios from 'axios';
import { TransportRoute, Bus, StudentTransport } from '../models/transport';

// Base URL me sirf ek hi dafa "transport" hoga
const API_BASE = "http://127.0.0.1:8000/api/transport";

const transportService = {
  // Routes
  getRoutes: () => axios.get<TransportRoute[]>(`${API_BASE}/transport/routes/`),
  createRoute: (data: TransportRoute) => axios.post(`${API_BASE}/transport/routes/`, data),
  updateRoute: (id: number, data: TransportRoute) => axios.put(`${API_BASE}/transport/routes/${id}/`, data),
  deleteRoute: (id: number) => axios.delete(`${API_BASE}/transport/routes/${id}/`),

  // Buses
  getBuses: () => axios.get<Bus[]>(`${API_BASE}/transport/buses/`),
  createBus: (data: Bus) => axios.post(`${API_BASE}/transport/buses/`, data),
  updateBus: (id: number, data: Bus) => axios.put(`${API_BASE}/transport/buses/${id}/`, data),
  deleteBus: (id: number) => axios.delete(`${API_BASE}/transport/buses/${id}/`),

  // Student Transport
  getStudentTransports: (params?: any) => axios.get<StudentTransport[]>(`${API_BASE}/transport/student/`, { params }),
  assignTransport: (payload: StudentTransport) => axios.post(`${API_BASE}/transport/student/`, payload),
  updateStudentTransport: (id: number, payload: StudentTransport) => axios.put(`${API_BASE}/transport/student/${id}/`, payload),
  deleteStudentTransport: (id: number) => axios.delete(`${API_BASE}/transport/student/${id}/`),
};

export default transportService;
