import { useAuth } from '../context/AuthContext';
import { useState, useEffect, useMemo, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Bar, Line, Pie } from 'react-chartjs-2';
import { messagingService, departmentService, courseService, studentService } from '../api/apiService';
import { instructorService } from '../api/studentInstructorService';
import { academicsService } from '../api/academicsService_enhanced';
import StudentManagement from '../components/pages/StudentManagement';
import DepartmentManagement from '../components/pages/DepartmentManagement';
import CourseManagement from '../components/pages/CourseManagement';
import TeacherManagement from '../components/pages/TeacherManagement';
import MessagingSystem from '../components/messaging/MessagingSystem';
import SystemHealthWidget from '../components/dashboard/SystemHealthWidget';
import { jsPDF } from 'jspdf';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const AdminDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [activeTab, setActiveTab] = useState<string>('dashboard');

  // Results tab state
  const [departments, setDepartments] = useState<any[]>([]);
  const [semesters, setSemesters] = useState<any[]>([]);
  const [students, setStudents] = useState<any[]>([]);
  const [selectedDepartment, setSelectedDepartment] = useState<number | null>(null);
  const [selectedSemester, setSelectedSemester] = useState<number | null>(null);
  const [selectedStudent, setSelectedStudent] = useState<number | null>(null);
  const [studentResults, setStudentResults] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);

  // New state for exam types and selected exam types
  const [examTypes, setExamTypes] = useState<string[]>([]);
  const [selectedExamTypes, setSelectedExamTypes] = useState<string[]>([]);

  // Add header animation effect
  useEffect(() => {
    const header = document.querySelector('.admin-header');
    if (header) {
      header.classList.add('animate-fadeIn');
    }
  }, []);

  // Admin data state
  const [adminData, setAdminData] = useState({
    users: [] as any[],
    departments: [] as any[],
    stats: {
      totalUsers: 1200,
      activeUsers: 850,
      totalDepartments: 25,
      totalCourses: 40,
      totalStudents: 1000,
      totalStaff: 200,
    },
  });

  // Fetch real data for dashboard stats
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const [studentsRes, departmentsRes, coursesRes, instructorsRes] = await Promise.all([
          studentService.getAllStudents(),
          departmentService.getAllDepartments(),
          courseService.getAllCourses(),
          instructorService.getAllInstructors(),
        ]);

        const totalUsers = studentsRes.data.length + instructorsRes.data.length;
        const activeUsers = Math.floor(totalUsers * 0.7); // Assuming 70% are active

        setAdminData({
          users: [...studentsRes.data, ...instructorsRes.data],
          departments: departmentsRes.data,
          stats: {
            totalUsers,
            activeUsers,
            totalDepartments: departmentsRes.data.length,
            totalCourses: coursesRes.data.length,
            totalStudents: studentsRes.data.length,
            totalStaff: instructorsRes.data.length,
          },
        });
      } catch (error) {
        console.error('Failed to fetch admin dashboard data:', error);
        // Keep default values if fetch fails
      }
    };

    fetchDashboardData();
  }, []);

  // Fetch departments for results tab
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await academicsService.getDepartments();
        setDepartments(response.data);
      } catch (error) {
        console.error('Failed to fetch departments:', error);
      }
    };

    if (activeTab === 'results') {
      fetchDepartments();
    }
  }, [activeTab]);

  // Fetch semesters when department is selected
  useEffect(() => {
    const fetchSemesters = async () => {
      if (!selectedDepartment) {
        setSemesters([]);
        setSelectedSemester(null);
        return;
      }

      try {
        const response = await academicsService.getSemestersByDepartment(selectedDepartment);
        setSemesters(response.data);
        setSelectedSemester(null); // Reset semester selection
        setSelectedStudent(null); // Reset student selection
        setStudentResults([]);
      } catch (error) {
        console.error('Failed to fetch semesters:', error);
        setSemesters([]);
      }
    };

    if (selectedDepartment) {
      fetchSemesters();
    }
  }, [selectedDepartment]);

  // Fetch students when semester is selected
  useEffect(() => {
    const fetchStudents = async () => {
      if (!selectedDepartment || !selectedSemester) {
        setStudents([]);
        setSelectedStudent(null);
        setStudentResults([]);
        return;
      }

      try {
        setLoading(true);
        // Use server-side filtering instead of client-side filtering
        const response = await studentService.getAllStudents({
          department: selectedDepartment,
          semester: selectedSemester
        });
        setStudents(response.data);
        setSelectedStudent(null); // Reset student selection
        setStudentResults([]);
      } catch (error) {
        console.error('Failed to fetch students:', error);
      } finally {
        setLoading(false);
      }
    };

    if (selectedDepartment && selectedSemester) {
      fetchStudents();
    }
  }, [selectedDepartment, selectedSemester]);

  // Normalize student results to ensure consistent course and GPA fields
  const normalizeStudentResults = (results: any[]): any[] => {
    return results.map((result) => {
      let courseName = 'N/A';
      let departmentName = 'N/A';
      let gpaValue = 'N/A';

      // Extract course name from course object or subject string
      if (result.course) {
        if (typeof result.course === 'object' && result.course !== null) {
          courseName = result.course.name || result.course.course_name || result.course.title || 'N/A';
        } else if (typeof result.course === 'string') {
          courseName = result.course;
        }
      } else if (result.subject) {
        if (typeof result.subject === 'string') {
          courseName = result.subject;
        }
      }

      // Extract department name from department object or string
      if (result.department) {
        if (typeof result.department === 'object' && result.department !== null) {
          departmentName = result.department.name || result.department.department_name || 'N/A';
        } else if (typeof result.department === 'string') {
          departmentName = result.department;
        }
      }

      // Extract GPA from percentage or other fields safely
      if (result.percentage !== undefined && result.percentage !== null) {
        gpaValue = result.percentage.toString();
      } else if (result.gpa !== undefined && result.gpa !== null) {
        gpaValue = result.gpa.toString();
      } else if (result.grade_point !== undefined && result.grade_point !== null) {
        gpaValue = result.grade_point.toString();
      } else if (result.grade_point_value !== undefined && result.grade_point_value !== null) {
        gpaValue = result.grade_point_value.toString();
      }

      return {
        ...result,
        normalizedCourseName: courseName,
        normalizedDepartmentName: departmentName,
        normalizedGPA: gpaValue,
      };
    });
  };

  // Fetch student results when student is selected
  useEffect(() => {
    const fetchStudentResults = async () => {
      if (!selectedStudent) return;

      try {
        setLoading(true);
        const response = await academicsService.getStudentResults(selectedStudent);
        console.log('Raw student results response:', response.data);
        let rawResults: any[] = [];

        // Extract results array from response.data object
        if (response.data && typeof response.data === 'object' && Array.isArray(response.data.results)) {
          rawResults = response.data.results;
        } else if (Array.isArray(response.data)) {
          rawResults = response.data;
        } else {
          rawResults = [];
          console.warn('Unexpected student results format:', response.data);
        }

        const normalizedResults = normalizeStudentResults(rawResults);
        setStudentResults(normalizedResults);

        // Extract unique exam types from results
        const uniqueExamTypes = Array.from(new Set(rawResults.map(r => r.exam_type).filter(Boolean)));
        setExamTypes(uniqueExamTypes);
        setSelectedExamTypes(uniqueExamTypes); // Select all by default
      } catch (error) {
