  // Helper function to safely get course name
  const getCourseName = (courseData: any): string => {
    if (!courseData) return 'N/A';

    try {
      // If it's an object with a name property, extract the name
      if (typeof courseData === 'object' && courseData !== null && 'name' in courseData) {
        return courseData.name || 'N/A';
      }

      // If it's an object with course_name property, extract the course_name
      if (typeof courseData === 'object' && courseData !== null && 'course_name' in courseData) {
        return courseData.course_name || 'N/A';
      }

      // If it's an object with title property
      if (typeof courseData === 'object' && courseData !== null && 'title' in courseData) {
        return courseData.title || 'N/A';
      }

      // If it's an object with code property
      if (typeof courseData === 'object' && courseData !== null && 'code' in courseData) {
        return courseData.code || 'N/A';
      }

      // If it's an object with subject property
      if (typeof courseData === 'object' && courseData !== null && 'subject' in courseData) {
        return courseData.subject || 'N/A';
      }

      // If it's a string, return as is
      if (typeof courseData === 'string') {
        return courseData || 'N/A';
      }

      // Fallback - if it's an object but doesn't have recognizable properties, return N/A
      if (typeof courseData === 'object' && courseData !== null) {
        return 'N/A';
      }

      // Fallback
      return 'N/A';
    } catch (error) {
      console.error('Error extracting course name:', error, courseData);
      return 'N/A';
    }
  };

  // Helper function to safely get course name from result object
  const getCourseNameFromResult = (result: any): string => {
    if (!result) return 'N/A';

    try {
      // If result.course_name is an object (full course object), extract the name
      if (result.course_name && typeof result.course_name === 'object' && result.course_name.name) {
        return result.course_name.name;
      }

      // If result.course_name is a string, return it
      if (typeof result.course_name === 'string') {
        return result.course_name;
      }

      // If result has a course property that's an object
      if (result.course && typeof result.course === 'object' && result.course.name) {
        return result.course.name;
      }

      // If result has a course property that's a string
      if (typeof result.course === 'string') {
        return result.course;
      }

      // If result has a course property that's an object with course_name
      if (result.course && typeof result.course === 'object' && result.course.course_name) {
        return result.course.course_name;
      }

      // If result has a course property that's an object with other properties
      if (result.course && typeof result.course === 'object') {
        // Try to find any string property that might be the course name
        const courseObj = result.course;
        if (courseObj.title) return courseObj.title;
        if (courseObj.code) return courseObj.code;
        if (courseObj.subject) return courseObj.subject;
        if (courseObj.id) return `Course ${courseObj.id}`;
      }

      // Additional checks for nested course info in result
      if (result.courseInfo && typeof result.courseInfo === 'object') {
        if (result.courseInfo.name) return result.courseInfo.name;
        if (result.courseInfo.title) return result.courseInfo.title;
        if (result.courseInfo.course_name) return result.courseInfo.course_name;
      }

      // Fallback to getCourseName function
      return getCourseName(result.course_name);
    } catch (error) {
      console.error('Error extracting course name from result:', error, result);
      return 'N/A';
    }
  };

  // Helper function to safely get department name
  const getDepartmentName = (departmentData: any): string => {
    if (!departmentData) return 'N/A';

    try {
      // If it's an object with a name property, extract the name
      if (typeof departmentData === 'object' && departmentData !== null && 'name' in departmentData) {
        return departmentData.name || 'N/A';
      }

      // If it's an object with department_name property
      if (typeof departmentData === 'object' && departmentData !== null && 'department_name' in departmentData) {
        return departmentData.department_name || 'N/A';
      }

      // If it's an object with title property
      if (typeof departmentData === 'object' && departmentData !== null && 'title' in departmentData) {
        return departmentData.title || 'N/A';
      }

      // If it's an object with code property
      if (typeof departmentData === 'object' && departmentData !== null && 'code' in departmentData) {
        return departmentData.code || 'N/A';
      }

      // If it's a string, return as is
      if (typeof departmentData === 'string') {
        return departmentData || 'N/A';
      }

      // If it's an object with other properties, try to find a suitable name
      if (typeof departmentData === 'object' && departmentData !== null) {
        if (departmentData.id) return `Department ${departmentData.id}`;
      }

      return 'N/A';
    } catch (error) {
      console.error('Error extracting department name:', error, departmentData);
      return 'N/A';
    }
