                      <th
                        key={examType}
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        {examType} Marks
                      </th>
                    ))}
                    {selectedExamTypes.map((examType) => (
                      <th
                        key={`${examType}-gpa`}
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        {examType} GPA
                      </th>
                    ))}
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {studentResults.map((result, index) => {
                    // Filter marks and GPA by selected exam types
                    const marksByExamType = selectedExamTypes.map((examType) => {
                      const examResult = studentResults.find(
                        (r) => r.course === result.course && r.exam_type === examType
                      );
                      return examResult ? examResult.marks || 'N/A' : 'N/A';
                    });
                    const gpaByExamType = selectedExamTypes.map((examType) => {
                    const examResult = studentResults.find(
                      (r) => r.course === result.course && r.exam_type === examType
                    );
                    return examResult ? examResult.gpa?.toString() || 'N/A' : 'N/A';
                  });

                    return (
                      <tr key={index} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {result.normalizedCourseName || 'N/A'}
                        </td>
                        {marksByExamType.map((marks, i) => (
                          <td key={`marks-${i}`} className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {marks}
                          </td>
                        ))}
                        {gpaByExamType.map((gpa, i) => (
                          <td key={`gpa-${i}`} className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {gpa}
                          </td>
                        ))}
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span
                            className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                              result.grade === 'A' || result.grade === 'A-' || result.grade === 'B+' || result.grade === 'B'
                                ? 'bg-green-100 text-green-800'
                                : result.grade === 'C' || result.grade === 'C-' || result.grade === 'D'
                                ? 'bg-yellow-100 text-yellow-800'
                                : 'bg-red-100 text-red-800'
                            }`}
                          >
                            {result.grade ? 'Completed' : 'Pending'}
                          </span>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <svg
                className="mx-auto h-12 w-12 text-gray-400"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"
                />
              </svg>
              <p className="mt-2">No results found for this student.</p>
            </div>
          )}
        </div>
      </motion.div>
    );
  };

  // Main render
  return (
    <div className="flex">
      {renderTabs()}
      <div className="flex-1 p-6 bg-gray-50 min-h-screen">
        <AnimatePresence>
          {activeTab === 'dashboard' && (
            <motion.div
              key="dashboard"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -20 }}
              transition={{ duration: 0.3 }}
            >
              {/* Pass stats as a single object prop to SystemHealthWidget */}
<SystemHealthWidget stats={adminData.stats} />
            </motion.div>
          )}
          {activeTab === 'students' && <StudentManagement activeTab={activeTab} />}
          {activeTab === 'instructors' && <TeacherManagement activeTab={activeTab} />}
          {activeTab === 'departments' && <DepartmentManagement activeTab={activeTab} />}
          {activeTab === 'courses' && <CourseManagement activeTab={activeTab} />}
          {activeTab === 'results' && renderResultsTab()}
          {activeTab === 'events' && <div>Events content coming soon...</div>}
          {activeTab === 'messaging' && <MessagingSystem />}
          {activeTab === 'scholarships' && <div>Scholarships content coming soon...</div>}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default AdminDashboard;
