import React from "react";
import { Book } from "../../models/library";

type Props = {
  book: Book;
  onBorrow: (id: number) => Promise<void>;
  onReturn: (id: number) => Promise<void>;
  onEdit?: (book: Book) => void;
  onDelete?: (id: number) => void;
  isAdmin?: boolean;
};

const BookCard: React.FC<Props> = ({ book, onBorrow, onReturn, onEdit, onDelete, isAdmin }) => {
  return (
    <div className="border rounded p-3 shadow-sm">
      <h3 className="font-semibold text-lg">{book.title}</h3>
      <p className="text-sm">Author: {book.author}</p>
      <p className="text-sm">ISBN: {book.isbn}</p>
      <p className="text-sm">Category: {book.category || "—"}</p>
      <p className="text-sm">Available: {book.available_copies}</p>

      <div className="mt-2 flex gap-2">
        {book.available_copies > 0 ? (
          <button onClick={() => onBorrow(book.id)} className="px-2 py-1 bg-green-600 text-white rounded">Borrow</button>
        ) : (
          <button disabled className="px-2 py-1 bg-gray-300 rounded">Not available</button>
        )}

        <button onClick={() => onReturn(book.id)} className="px-2 py-1 bg-yellow-500 text-white rounded">Return</button>

        {isAdmin && (
          <>
            <button onClick={() => onEdit && onEdit(book)} className="px-2 py-1 bg-blue-600 text-white rounded">Edit</button>
            <button onClick={() => onDelete && onDelete(book.id)} className="px-2 py-1 bg-red-600 text-white rounded">Delete</button>
          </>
        )}
      </div>
    </div>
  );
};

export default BookCard;
