import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const StudentDashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const studentId = "gen002"; // ← later link with backend auth

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await axios.get(
          `http://127.0.0.1:8000/api/academics/dashboard/${studentId}/`
        );
        setDashboardData(response.data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [studentId]);

  const resultChartData = useMemo(() => {
    const results = dashboardData?.results || [];
    return {
      labels: results.map((r: any) => r.course_name || "N/A"),
      datasets: [
        {
          label: "Grades %",
          data: results.map((r: any) => r.obtained_marks || 0),
          backgroundColor: "rgba(99, 102, 241, 0.7)",
          borderRadius: 8,
        },
      ],
    };
  }, [dashboardData]);

  const attendanceChartData = useMemo(() => {
    const a = dashboardData?.attendance_summary || { total_classes: 0, present_classes: 0 };
    return {
      labels: ["Total Classes", "Present Classes"],
      datasets: [
        {
          data: [a.total_classes, a.present_classes],
          backgroundColor: ["#d1d5db", "#4f46e5"],
          borderWidth: 2,
        },
      ],
    };
  }, [dashboardData]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-indigo-600">
        Loading Dashboard...
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-red-600">
        Failed to load dashboard data.
      </div>
    );
  }

  const student = dashboardData.student_info;
  const attendance = dashboardData.attendance_summary;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-white p-6 md:p-10 space-y-10">
      {/* Header */}
      <div className="text-center mb-10">
        <h1 className="text-4xl font-extrabold text-indigo-700 tracking-tight">
          🎓 Student Dashboard
        </h1>
        <p className="text-gray-600 mt-2 text-lg">
          Welcome back, <span className="font-semibold">{student?.name}</span> 👋
        </p>
      </div>

      {/* Student Info Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {[
          { label: "Name", value: student?.name },
          { label: "Email", value: student?.email },
          { label: "Department", value: student?.department },
          { label: "Semester", value: student?.semester },
          { label: "GPA", value: student?.gpa },
          { label: "CGPA", value: student?.cgpa },
        ].map((item, idx) => (
          <div
            key={idx}
            className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition p-5 border-t-4 border-indigo-500 overflow-hidden"
          >
            <h3 className="font-semibold text-gray-600">{item.label}</h3>
            <p className="text-gray-800 font-medium truncate">{item.value || "—"}</p>
          </div>
        ))}
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-10">
        {/* Attendance Chart */}
        <div className="bg-white p-6 shadow-lg rounded-2xl border border-gray-100 hover:shadow-xl transition">
          <h2 className="text-lg font-bold text-gray-700 mb-4 flex items-center gap-2">
            <span>📊</span> Attendance Summary
          </h2>
          <div className="flex justify-center">
            <div className="w-60">
              <Doughnut data={attendanceChartData} />
            </div>
          </div>
          <p className="text-center mt-5 text-sm text-gray-500">
            Attendance: <span className="font-semibold">{attendance?.percentage || 0}%</span>
          </p>
        </div>

        {/* Results Chart */}
        <div className="bg-white p-6 shadow-lg rounded-2xl border border-gray-100 hover:shadow-xl transition">
          <h2 className="text-lg font-bold text-gray-700 mb-4 flex items-center gap-2">
            <span>📘</span> Results Overview
          </h2>
          <div className="h-72">
            <Bar
              data={resultChartData}
              options={{
                responsive: true,
                plugins: {
                  legend: { display: false },
                },
                scales: {
                  y: { beginAtZero: true, grid: { color: "#f3f4f6" } },
                  x: { grid: { display: false } },
                },
              }}
            />
          </div>
        </div>
      </div>

      {/* Fees Record */}
      <div className="bg-white p-6 shadow-lg rounded-2xl border border-gray-100 mt-10">
        <h2 className="text-lg font-bold text-gray-700 mb-4 flex items-center gap-2">
          <span>💰</span> Fees Record
        </h2>
        {dashboardData.fees.length === 0 ? (
          <p className="text-gray-500 text-center py-6">No fee records available yet.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full border text-sm text-center">
              <thead>
                <tr className="bg-indigo-100 text-gray-700">
                  <th className="p-3 border">Description</th>
                  <th className="p-3 border">Amount</th>
                  <th className="p-3 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {dashboardData.fees.map((f: any, idx: number) => (
                  <tr
                    key={idx}
                    className="border-t hover:bg-indigo-50 transition text-gray-600"
                  >
                    <td className="p-3 border">{f.description}</td>
                    <td className="p-3 border font-medium">Rs. {f.amount}</td>
                    <td
                      className={`p-3 border font-semibold ${
                        f.status === "Paid" ? "text-green-600" : "text-red-600"
                      }`}
                    >
                      {f.status}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
