import React, { useState, useEffect } from 'react';
import transportService from '../../api/transportService';
import { TransportRoute, TransportRoutePayload } from '../../models/transport';

type Props = { isOpen: boolean; onClose: () => void; onSaved: () => void; initial?: TransportRoute };

const AddRouteModal: React.FC<Props> = ({ isOpen, onClose, onSaved, initial }) => {
  const [name, setName] = useState(initial?.name || '');
  const [start, setStart] = useState(initial?.start_point || '');
  const [end, setEnd] = useState(initial?.end_point || '');
  const [stops, setStops] = useState(initial?.stops || '');
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    setName(initial?.name || '');
    setStart(initial?.start_point || '');
    setEnd(initial?.end_point || '');
    setStops(initial?.stops || '');
  }, [initial]);

  if (!isOpen) return null;

  const submit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSaving(true);
    try {
      const payload: TransportRoutePayload = { name, start_point: start, end_point: end, stops };

      if (initial?.id) await transportService.updateRoute(initial.id, payload);
      else await transportService.createRoute(payload);

      onSaved();
      onClose();
    } catch (err) {
      console.error(err);
      alert('Save failed');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center">
      <form onSubmit={submit} className="bg-white p-6 rounded w-[520px]">
        <h3 className="text-xl font-semibold mb-4">{initial?.id ? 'Edit Route' : 'Add Route'}</h3>
        <div className="grid gap-3">
          <input required value={name} onChange={(e) => setName(e.target.value)} placeholder="Route name" className="p-2 border" />
          <input required value={start} onChange={(e) => setStart(e.target.value)} placeholder="Start point" className="p-2 border" />
          <input required value={end} onChange={(e) => setEnd(e.target.value)} placeholder="End point" className="p-2 border" />
          <textarea value={stops} onChange={(e) => setStops(e.target.value)} placeholder="Stops (comma separated)" className="p-2 border" />
        </div>

        <div className="mt-4 flex justify-end gap-2">
          <button type="button" onClick={onClose} className="px-4 py-2 border rounded">Cancel</button>
          <button type="submit" disabled={saving} className="px-4 py-2 bg-indigo-600 text-white rounded">
            {saving ? 'Saving...' : 'Save'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddRouteModal;
