import React, { useState, useEffect } from 'react';
import { studentService, departmentService, semesterService, courseService } from '../../api/apiService';

interface Semester {
  semester_id: number;
  name: string;
  semester_code: string;
  department: number;
}

interface Course {
  course_id: number;
  name: string;
  code: string;
  semester_details?: {
    semester_id: number;
    name: string;
    semester_code: string;
  };
}

interface StudentModalProps {
  isOpen: boolean;
  onClose: () => void;
  studentId?: number;
  onSuccess: () => void;
  preSelectedDepartment?: number; // Optional prop to pre-select department
  preSelectedSemester?: number; // Optional prop to pre-select semester
}

interface Department {
  department_id: number;
  name: string;
  code: string;
}

const StudentModal: React.FC<StudentModalProps> = ({ isOpen, onClose, studentId, onSuccess, preSelectedDepartment }): React.ReactElement | null => {
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [semesters, setSemesters] = useState<Semester[]>([]);
  const [courses, setCourses] = useState<Course[]>([]);
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string | undefined>(undefined);

  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    registration_number: '',
    department: 0, // Department field
    semester: 0, // Semester field
    batch: '', // Batch field
    guardian_name: '',
    guardian_contact: '',
    address: '',
    date_of_birth: '',
    gender: 'male',
    blood_group: '',
    phone: '',
  });

  // Fetch departments
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await departmentService.getAllDepartments();
        if (response.data && Array.isArray(response.data)) {
          setDepartments(response.data);
          if (preSelectedDepartment) {
            setFormData(prev => ({ ...prev, department: preSelectedDepartment }));
          }
        } else {
          setError('Failed to load departments: Invalid data format');
        }
      } catch (error) {
        setError('Failed to load departments. Please check your connection and try again.');
      }
    };

    fetchDepartments();
  }, [preSelectedDepartment]);

  // Fetch semesters when department changes
  useEffect(() => {
    const fetchSemesters = async () => {
      if (formData.department) {
        try {
          const response = await departmentService.getSemestersByDepartment(formData.department);
          if (response.data && Array.isArray(response.data)) {
            const filteredSemesters = response.data.filter((sem: any) => {
              const semNumberMatch = sem.name.match(/(\d+)/);
              if (semNumberMatch) {
                const semNumber = parseInt(semNumberMatch[1], 10);
                return semNumber >= 1 && semNumber <= 8;
              }
              return false;
            });
            setSemesters(filteredSemesters);
          } else {
            setError('Failed to load semesters: Invalid data format');
          }
        } catch (error) {
          setError('Failed to load semesters. Please check your connection and try again.');
        }
      } else {
        setSemesters([]);
      }
    };

    fetchSemesters();
  }, [formData.department]);

  // Fetch courses when semester changes
  useEffect(() => {
    const fetchCourses = async () => {
      if (formData.semester) {
        try {
          const response = await courseService.getAllCourses();
          if (response.data && Array.isArray(response.data)) {
            const filteredCourses = response.data.filter((course: Course) =>
              course.semester_details?.semester_id === formData.semester
            );
            setCourses(filteredCourses);
          } else {
            setError('Failed to load courses: Invalid data format');
          }
        } catch (error) {
          setError('Failed to load courses. Please check your connection and try again.');
        }
      } else {
        setCourses([]);
      }
    };

    fetchCourses();
  }, [formData.semester]);

  // Fetch student data if editing
  useEffect(() => {
    if (studentId) {
      const fetchStudentData = async () => {
        setIsLoading(true);
        try {
          const response = await studentService.getStudentById(studentId);
          const student = response.data;

          setFormData({
            first_name: student.first_name || '',
            last_name: student.last_name || '',
            email: student.email || '',
            password: '',
            registration_number: student.registration_number || '',
            department: student.department ? student.department.department_id : 0,
            semester: student.semester ? student.semester.semester_id : 0,
            batch: student.batch || '',
            guardian_name: student.guardian_name || '',
            guardian_contact: student.guardian_contact || '',
            address: student.address || '',
            date_of_birth: student.date_of_birth ? new Date(student.date_of_birth).toISOString().split('T')[0] : '',
            gender: student.gender || 'male',
            blood_group: student.blood_group || '',
            phone: student.phone || '',
          });

          if (student.image) {
            setImagePreview(student.image);
          }
        } catch (error) {
          setError('Failed to fetch student data');
        } finally {
          setIsLoading(false);
        }
      };

      fetchStudentData();
    }
  }, [studentId]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    if (name === 'department' || name === 'semester') {
      setFormData(prev => ({ ...prev, [name]: value ? parseInt(value, 10) : null }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImageFile(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      if (!formData.first_name.trim()) {
        setError('First name is required.');
        setIsLoading(false);
        return;
      }
      if (!formData.last_name.trim()) {
        setError('Last name is required.');
        setIsLoading(false);
        return;
      }
      if (!formData.email.trim()) {
        setError('Email is required.');
        setIsLoading(false);
        return;
      }
      if (!formData.phone.trim()) {
        setError('Phone number is required.');
        setIsLoading(false);
        return;
      }
      if (!formData.date_of_birth) {
        setError('Date of birth is required.');
        setIsLoading(false);
        return;
      }
      if (!formData.registration_number.trim()) {
        setError('Registration number is required.');
        setIsLoading(false);
        return;
      }
      if (!studentId && !formData.password.trim()) {
        setError('Password is required for new students.');
        setIsLoading(false);
        return;
      }

      const dataToSend: any = { ...formData };

      if (dataToSend.department === 0 || dataToSend.department === "0" || !dataToSend.department) {
        dataToSend.department_id = null;
        delete dataToSend.department;
      } else {
        dataToSend.department_id = parseInt(dataToSend.department, 10);
        delete dataToSend.department;
      }

      if (dataToSend.semester === 0 || dataToSend.semester === "0" || !dataToSend.semester) {
        dataToSend.semester_id = null;
        delete dataToSend.semester;
      } else {
        dataToSend.semester_id = parseInt(dataToSend.semester, 10);
        delete dataToSend.semester;
      }

      let response;
      if (studentId) {
        response = await studentService.updateStudent(studentId, dataToSend);
      } else {
        response = await studentService.createStudent(dataToSend);
      }

      if (imageFile && (response.data.id || response.data.student_id)) {
        const studentId = response.data.id || response.data.student_id;
        const imageFormData = new FormData();
        imageFormData.append('image', imageFile);
        await studentService.uploadStudentImage(studentId, imageFormData);
        const updatedStudentResponse = await studentService.getStudentById(studentId);
        if (updatedStudentResponse.data.image) {
          setImagePreview(updatedStudentResponse.data.image);
        }
      }

      onSuccess();
      onClose();
    } catch (error: any) {
      setError(error.response?.data?.message || 'Failed to save student');
    } finally {
      setIsLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-4xl max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-800">
              {studentId ? 'Edit Student' : 'Add New Student'}
            </h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="md:col-span-1">
                <div className="mb-6">
                  <label className="block text-gray-700 text-sm font-bold mb-2">
                    Profile Picture
                  </label>
                  <div className="flex flex-col items-center">
                    <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center overflow-hidden mb-2">
                      {imagePreview ? (
                        <img src={imagePreview} alt="Preview" className="w-full h-full object-cover" />
                      ) : (
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                        </svg>
                      )}
                    </div>
                    <label className="cursor-pointer bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded-md text-sm transition-colors">
                      Upload Photo
                      <input
                        type="file"
                        accept="image/*"
                        className="hidden"
                        onChange={handleImageChange}
                      />
                    </label>
                  </div>
                </div>
              </div>

              <div className="md:col-span-2">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="first_name">
                      First Name *
                    </label>
                    <input
                      id="first_name"
                      name="first_name"
                      type="text"
                      required
                      value={formData.first_name}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="last_name">
                      Last Name *
                    </label>
                    <input
                      id="last_name"
                      name="last_name"
                      type="text"
                      required
                      value={formData.last_name}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
                      Email *
                    </label>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      required
                      value={formData.email}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
                      {studentId ? 'Password (leave blank to keep current)' : 'Password *'}
                    </label>
                    <input
                      id="password"
                      name="password"
                      type="password"
                      required={!studentId}
                      value={formData.password}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="registration_number">
                      Registration Number *
                    </label>
                    <input
                      id="registration_number"
                      name="registration_number"
                      type="text"
                      required
                      value={formData.registration_number}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="batch">
                      Batch
                    </label>
                    <input
                      id="batch"
                      name="batch"
                      type="text"
                      value={formData.batch}
                      onChange={handleChange}
                      placeholder="e.g., 2025-2029"
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="department">
                      Department *
                    </label>
                    <select
                      id="department"
                      name="department"
                      required
                      value={formData.department || ""}
                      onChange={handleChange}
                      className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${departments.length === 0 ? 'bg-gray-100' : ''}`}
                      disabled={departments.length === 0}
                    >
                      <option value="">Select Department</option>
                      {departments.length > 0 ? (
                        departments.map(dept => (
                          <option key={dept.department_id} value={dept.department_id}>{dept.name} ({dept.code})</option>
                        ))
                      ) : (
                        <option value="" disabled>Loading departments...</option>
                      )}
                    </select>
                    {departments.length === 0 && !error && (
                      <p className="text-blue-500 text-xs italic mt-1">Loading departments...</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="semester">
                      Semester *
                    </label>
                    <select
                      id="semester"
                      name="semester"
                      required
                      value={formData.semester || ""}
                      onChange={handleChange}
                      className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${semesters.length === 0 ? 'bg-gray-100' : ''}`}
                      disabled={semesters.length === 0 || !formData.department}
                    >
                      <option value="">Select Semester</option>
                      {semesters.length > 0 ? (
                        semesters.map(semester => (
                          <option key={semester.semester_id} value={semester.semester_id}>{semester.name} ({semester.semester_code})</option>
                        ))
                      ) : (
                        <option value="" disabled>
                          {formData.department ? 'Loading semesters...' : 'Select department first'}
                        </option>
                      )}
                    </select>
                    {semesters.length === 0 && !error && (
                      <p className="text-blue-500 text-xs italic mt-1">
                        {formData.department ? 'Loading semesters...' : 'Please select a department first'}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="date_of_birth">
                      Date of Birth
                    </label>
                    <input
                      id="date_of_birth"
                      name="date_of_birth"
                      type="date"
                      value={formData.date_of_birth}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="gender">
                      Gender
                    </label>
                    <select
                      id="gender"
                      name="gender"
                      value={formData.gender}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    >
                      <option value="male">Male</option>
                      <option value="female">Female</option>
                      <option value="other">Other</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="blood_group">
                      Blood Group
                    </label>
                    <input
                      id="blood_group"
                      name="blood_group"
                      type="text"
                      value={formData.blood_group}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="phone">
                      Phone
                    </label>
                    <input
                      id="phone"
                      name="phone"
                      type="tel"
                      value={formData.phone}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="guardian_name">
                      Guardian Name
                    </label>
                    <input
                      id="guardian_name"
                      name="guardian_name"
                      type="text"
                      value={formData.guardian_name}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>

                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="guardian_contact">
                      Guardian Contact
                    </label>
                    <input
                      id="guardian_contact"
                      name="guardian_contact"
                      type="text"
                      value={formData.guardian_contact}
                      onChange={handleChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>
                </div>

                <div className="mt-6">
                  <h3 className="text-lg font-semibold text-gray-800 mb-2">Assigned Courses</h3>
                  <ul className="list-disc list-inside max-h-48 overflow-y-auto border border-gray-300 rounded p-3 bg-gray-50">
                    {courses.map(course => (
                      <li key={course.course_id} className="text-gray-700">
                        {course.name} ({course.code})
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>

            <div className="mt-8 flex justify-end space-x-3">
              <button
                type="button"
                onClick={onClose}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isLoading}
                className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition-colors flex items-center"
              >
                {isLoading && (
                  <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                )}
                {studentId ? 'Update Student' : 'Add Student'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default StudentModal;
