import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const StudentDashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  const studentId = "gen002"; // ← backend se link krna hoga later

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await axios.get(
         ` http://127.0.0.1:8000/api/academics/dashboard/${studentId}/`
        );
        setDashboardData(response.data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [studentId]);

  // ✅ Charts hooks must be defined before any conditional return
  const resultChartData = useMemo(() => {
    const results = dashboardData?.results || [];
    return {
      labels: results.map((r: any) => r.course_name || "N/A"),
      datasets: [
        {
          label: "Grades %",
          data: results.map((r: any) => r.obtained_marks || 0),
          backgroundColor: "rgba(99, 102, 241, 0.6)",
        },
      ],
    };
  }, [dashboardData]);

  const attendanceChartData = useMemo(() => {
    const a = dashboardData?.attendance_summary || { total_classes: 0, present_classes: 0 };
    return {
      labels: ["Total Classes", "Present Classes"],
      datasets: [
        {
          label: "Attendance",
          data: [a.total_classes, a.present_classes],
          backgroundColor: ["rgba(54,162,235,0.6)", "rgba(75,192,192,0.6)"],
        },
      ],
    };
  }, [dashboardData]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold">
        Loading Dashboard...
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-red-600">
        Failed to load dashboard data.
      </div>
    );
  }

  const student = dashboardData.student_info;
  const attendance = dashboardData.attendance_summary;

  return (
    <div className="p-6 space-y-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-indigo-700">🎓 Student Dashboard</h1>
        <p className="text-gray-600 mt-2">Welcome, {student?.name || "Student"}</p>
      </div>

      {/* Student Info */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600">
          <h2 className="font-semibold text-gray-700">Name</h2>
          <p>{student?.name}</p>
        </div>
        <div className="bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600">
          <h2 className="font-semibold text-gray-700">Email</h2>
          <p>{student?.email}</p>
        </div>
        <div className="bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600">
          <h2 className="font-semibold text-gray-700">Department</h2>
          <p>{student?.department}</p>
        </div>
        <div className="bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600">
          <h2 className="font-semibold text-gray-700">Semester</h2>
          <p>{student?.semester}</p>
        </div>
        <div className="bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600">
          <h2 className="font-semibold text-gray-700">GPA</h2>
          <p>{student?.gpa}</p>
        </div>
        <div className="bg-white shadow rounded-2xl p-4 border-l-4 border-indigo-600">
          <h2 className="font-semibold text-gray-700">CGPA</h2>
          <p>{student?.cgpa}</p>
        </div>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-8">
        <div className="bg-white p-6 shadow rounded-2xl">
          <h2 className="text-lg font-bold text-gray-700 mb-4">📊 Attendance Summary</h2>
          <Doughnut data={attendanceChartData} />
          <p className="text-center mt-4 text-sm text-gray-500">
            Attendance Percentage: {attendance?.percentage || 0}%
          </p>
        </div>

        <div className="bg-white p-6 shadow rounded-2xl">
          <h2 className="text-lg font-bold text-gray-700 mb-4">📘 Results Overview</h2>
          <Bar data={resultChartData} />
        </div>
      </div>

      {/* Fees Section */}
      <div className="bg-white p-6 shadow rounded-2xl mt-8">
        <h2 className="text-lg font-bold text-gray-700 mb-4">💰 Fees Record</h2>
        {dashboardData.fees.length === 0 ? (
          <p className="text-gray-500">No fee records available yet.</p>
        ) : (
          <table className="w-full border text-sm">
            <thead>
              <tr className="bg-indigo-100">
                <th className="p-2 border">Description</th>
                <th className="p-2 border">Amount</th>
                <th className="p-2 border">Status</th>
              </tr>
            </thead>
            <tbody>
              {dashboardData.fees.map((f: any, idx: number) => (
                <tr key={idx} className="text-center">
                  <td className="p-2 border">{f.description}</td>
                  <td className="p-2 border">Rs. {f.amount}</td>
                  <td
                    className={`p-2 border ${
                      f.status === "Paid" ? "text-green-600" : "text-red-600"
                    }`}
                  >
                    {f.status}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
