import { useAuth } from '../context/AuthContext';
import { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Bar, Line } from 'react-chartjs-2';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const StudentDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [dashboardData, setDashboardData] = useState<any>(null);

  // Fetch Student Dashboard Data
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const res = await axios.get(
          `http://127.0.0.1:8000/api/academics/dashboard/${currentUser?.student_id}/`
        );
        setDashboardData(res.data);
      } catch (error) {
        console.error('Failed to fetch student dashboard:', error);
      }
    };

    if (currentUser?.student_id) {
      fetchDashboardData();
    }
  }, [currentUser]);

  // Tabs
  const tabs = useMemo(() => {
    return [
      { id: 'overview', label: 'Dashboard Overview', icon: 'M3 12l2-2m0 0l7-7 7 7M5 10v10h3m10-11l2 2m-2-2v10h-3' },
      { id: 'attendance', label: 'Attendance', icon: 'M9 12h6m2 10H7a2 2 0 01-2-2V8h14v12a2 2 0 01-2 2z' },
      { id: 'results', label: 'Results', icon: 'M9 17v2h6v-2m-7-8h8' },
      { id: 'fees', label: 'Fee Details', icon: 'M12 8c-1.657 0-3 1.343-3 3v5h6v-5c0-1.657-1.343-3-3-3z' },
    ];
  }, []);

  // Chart Data
  const attendanceData = useMemo(() => {
    if (!dashboardData) return null;
    return {
      labels: ['Present', 'Absent'],
      datasets: [
        {
          data: [
            dashboardData.attendance_summary.present_classes,
            dashboardData.attendance_summary.total_classes -
              dashboardData.attendance_summary.present_classes,
          ],
          backgroundColor: ['#4F46E5', '#E5E7EB'],
        },
      ],
    };
  }, [dashboardData]);

  const performanceData = useMemo(() => {
    if (!dashboardData || dashboardData.results.length === 0) return null;
    return {
      labels: dashboardData.results.map((r: any) => r.course_name),
      datasets: [
        {
          label: 'GPA',
          data: dashboardData.results.map((r: any) => r.gpa || 0),
          backgroundColor: 'rgba(99, 102, 241, 0.6)',
          borderColor: '#6366F1',
          borderWidth: 2,
        },
      ],
    };
  }, [dashboardData]);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-gradient-to-b from-indigo-800 to-indigo-900 text-white p-4 space-y-4 shadow-xl">
        <div className="text-center mb-6">
          <div className="h-16 w-16 mx-auto bg-white text-indigo-700 rounded-full flex items-center justify-center text-2xl font-bold">
            {currentUser?.username?.[0]?.toUpperCase() || 'S'}
          </div>
          <h3 className="text-lg font-semibold mt-2">
            {currentUser?.username || 'Student'}
          </h3>
          <p className="text-xs text-indigo-200">Student Portal</p>
        </div>

        <nav>
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`w-full flex items-center px-4 py-2 mb-2 rounded-lg transition-colors ${
                activeTab === tab.id
                  ? 'bg-indigo-700 text-white'
                  : 'hover:bg-indigo-700 text-indigo-200'
              }`}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-5 w-5 mr-3"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={tab.icon} />
              </svg>
              {tab.label}
            </button>
          ))}
        </nav>

        <button
          onClick={logout}
          className="w-full mt-6 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg font-medium transition-colors duration-200"
        >
          Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <header className="bg-gradient-to-r from-indigo-600 to-purple-600 shadow-lg px-6 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-white">
            {tabs.find((t) => t.id === activeTab)?.label}
          </h1>
          <p className="text-indigo-100">
            Welcome back, {currentUser?.username || 'Student'}
          </p>
        </header>

        <main className="p-6">
          <AnimatePresence mode="wait">
            {activeTab === 'overview' && dashboardData && (
              <motion.div
                key="overview"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Stats Cards */}
                <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-6">
                  <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-100">
                    <p className="text-sm text-gray-600">CGPA</p>
                    <p className="text-2xl font-bold text-indigo-600">
                      {dashboardData.student_info.cgpa.toFixed(2)}
                    </p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-100">
                    <p className="text-sm text-gray-600">Current GPA</p>
                    <p className="text-2xl font-bold text-indigo-600">
                      {dashboardData.student_info.gpa.toFixed(2)}
                    </p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-100">
                    <p className="text-sm text-gray-600">Attendance</p>
                    <p className="text-2xl font-bold text-indigo-600">
                      {dashboardData.attendance_summary.percentage}%
                    </p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-100">
                    <p className="text-sm text-gray-600">Department</p>
                    <p className="text-lg font-semibold text-indigo-700">
                      {dashboardData.student_info.department || 'N/A'}
                    </p>
                  </div>
                </div>

                {/* Charts */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="bg-white p-4 rounded-xl shadow-lg border border-gray-100">
                    <h3 className="text-lg font-semibold mb-2">Attendance Summary</h3>
                    <div className="h-48">
                      {attendanceData && <Bar data={attendanceData} />}
                    </div>
                  </div>

                  <div className="bg-white p-4 rounded-xl shadow-lg border border-gray-100">
                    <h3 className="text-lg font-semibold mb-2">Performance Overview</h3>
                    <div className="h-48">
                      {performanceData ? (
                        <Line data={performanceData} />
                      ) : (
                        <p className="text-gray-500 text-center mt-12">
                          No results uploaded yet.
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </main>
      </div>
    </div>
  );
};

export default StudentDashboard;
