import { useAuth } from "../context/AuthContext";
import { useState, useEffect, useMemo } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Bar, Line } from "react-chartjs-2";
import { studentService, resultService, courseService } from "../api/apiService";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

const StudentDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [activeTab, setActiveTab] = useState<string>("dashboard");

  const [studentData, setStudentData] = useState({
    courses: [] as any[],
    results: [] as any[],
    stats: {
      gpa: 0,
      totalCourses: 0,
      avgAttendance: 0,
    },
  });

  useEffect(() => {
    const fetchStudentData = async () => {
      try {
        if (!currentUser?.id) return;

        // ✅ Get student profile
        const studentRes = await studentService.getStudentById(currentUser.id);
        const student = studentRes.data;

        // ✅ Fetch related data
        const [coursesRes, resultsRes] = await Promise.all([
          courseService.getCoursesBySemester(student?.semester?.id || 0),
          resultService.getStudentResults(currentUser.id),
        ]);

        // ✅ Temporary attendance mock (random for now)
        const fakeAttendance = coursesRes.data.map((course: any) => ({
          course_name: course.name,
          attendance_rate: Math.floor(Math.random() * 21) + 80, // 80–100%
        }));

        // ✅ Stats calculation
        const totalCourses = coursesRes.data.length;
        const avgAttendance =
          fakeAttendance.reduce((acc: number, record: any) => acc + record.attendance_rate, 0) /
          (fakeAttendance.length || 1);
        const gpa =
          resultsRes.data.reduce((acc: number, res: any) => acc + (res.gpa || 0), 0) /
          (resultsRes.data.length || 1);

        setStudentData({
          courses: coursesRes.data,
          results: resultsRes.data,
          stats: { gpa, totalCourses, avgAttendance },
        });
      } catch (error) {
        console.error("Error loading student dashboard data:", error);
      }
    };

    fetchStudentData();
  }, [currentUser]);

  // Tabs
  const tabs = useMemo(
    () => [
      { id: "dashboard", label: "Dashboard" },
      { id: "results", label: "My Results" },
      { id: "attendance", label: "My Attendance" },
    ],
    []
  );

  // Charts
  const resultChartData = useMemo(
    () => ({
      labels: studentData.results.map((r: any) => r.course_name),
      datasets: [
        {
          label: "Grades",
          data: studentData.results.map((r: any) => r.grade_percentage || 0),
          backgroundColor: "rgba(75,192,192,0.6)",
        },
      ],
    }),
    [studentData.results]
  );

  const attendanceChartData = useMemo(
    () => ({
      labels: studentData.courses.map((c: any) => c.name),
      datasets: [
        {
          label: "Attendance %",
          data: studentData.courses.map(() => Math.floor(Math.random() * 21) + 80),
          borderColor: "rgba(54,162,235,1)",
          backgroundColor: "rgba(54,162,235,0.2)",
          tension: 0.4,
        },
      ],
    }),
    [studentData.courses]
  );

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: { legend: { position: "top" as const } },
  };

  const renderTabs = () => (
    <div className="w-64 bg-gradient-to-b from-blue-700 to-indigo-900 text-white p-4 min-h-screen">
      <div className="mb-8 text-center">
        <div className="h-16 w-16 rounded-full bg-white mx-auto mb-2 flex items-center justify-center">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-10 w-10 text-indigo-700"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 12c2.21 0 4-1.79 4-4S14.21 4 12 4 8 5.79 8 8s1.79 4 4 4z"
            />
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M6 20a6 6 0 1112 0H6z"
            />
          </svg>
        </div>
        <h3 className="text-lg font-semibold">{currentUser?.username || "Student"}</h3>
        <p className="text-xs text-indigo-200">Student Portal</p>
      </div>
      <ul className="space-y-2">
        {tabs.map((tab) => (
          <li key={tab.id}>
            <button
              onClick={() => setActiveTab(tab.id)}
              className={`w-full text-left px-4 py-2 rounded-lg transition ${
                activeTab === tab.id ? "bg-indigo-700" : "hover:bg-indigo-700"
              }`}
            >
              {tab.label}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );

  return (
    <div className="flex h-screen bg-gray-100">
      {renderTabs()}
      <div className="flex-1 overflow-auto">
        <header className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4 text-white flex justify-between items-center">
          <h1 className="text-2xl font-bold">
            {tabs.find((t) => t.id === activeTab)?.label || "Dashboard"}
          </h1>
          <button
            onClick={logout}
            className="bg-red-500 hover:bg-red-600 px-4 py-2 rounded-lg text-sm font-medium"
          >
            Logout
          </button>
        </header>

        <main className="p-6">
          <AnimatePresence mode="wait">
            {/* DASHBOARD TAB */}
            {activeTab === "dashboard" && (
              <motion.div
                key="dashboard"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                  <div className="bg-white p-6 rounded-xl shadow-lg">
                    <h3 className="text-sm text-gray-600">Total Courses</h3>
                    <p className="text-2xl font-bold">{studentData.stats.totalCourses}</p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow-lg">
                    <h3 className="text-sm text-gray-600">Average Attendance</h3>
                    <p className="text-2xl font-bold">
                      {studentData.stats.avgAttendance.toFixed(1)}%
                    </p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow-lg">
                    <h3 className="text-sm text-gray-600">Current GPA</h3>
                    <p className="text-2xl font-bold">
                      {studentData.stats.gpa.toFixed(2)}
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div className="bg-white p-4 rounded-xl shadow-lg">
                    <h3 className="text-lg font-semibold mb-2">Grade Overview</h3>
                    <div className="h-48">
                      <Bar data={resultChartData} options={chartOptions} />
                    </div>
                  </div>
                  <div className="bg-white p-4 rounded-xl shadow-lg">
                    <h3 className="text-lg font-semibold mb-2">Attendance Overview</h3>
                    <div className="h-48">
                      <Line data={attendanceChartData} options={chartOptions} />
                    </div>
                  </div>
                </div>
              </motion.div>
            )}

            {/* RESULTS TAB */}
            {activeTab === "results" && (
              <motion.div key="results" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                <h2 className="text-xl font-semibold mb-4">My Results</h2>
                <table className="min-w-full bg-white rounded-xl shadow-lg">
                  <thead>
                    <tr className="bg-indigo-100 text-gray-800">
                      <th className="py-2 px-4">Course</th>
                      <th className="py-2 px-4">Grade</th>
                      <th className="py-2 px-4">GPA</th>
                    </tr>
                  </thead>
                  <tbody>
                    {studentData.results.map((r: any, idx: number) => (
                      <tr key={idx} className="border-t text-center">
                        <td className="py-2 px-4">{r.course_name}</td>
                        <td className="py-2 px-4">{r.grade}</td>
                        <td className="py-2 px-4">{r.gpa}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </motion.div>
            )}

            {/* ATTENDANCE TAB */}
            {activeTab === "attendance" && (
              <motion.div key="attendance" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                <h2 className="text-xl font-semibold mb-4">My Attendance</h2>
                <table className="min-w-full bg-white rounded-xl shadow-lg">
                  <thead>
                    <tr className="bg-indigo-100 text-gray-800">
                      <th className="py-2 px-4">Course</th>
                      <th className="py-2 px-4">Attendance %</th>
                    </tr>
                  </thead>
                  <tbody>
                    {studentData.courses.map((c: any, idx: number) => (
                      <tr key={idx} className="border-t text-center">
                        <td className="py-2 px-4">{c.name}</td>
                        <td className="py-2 px-4">{Math.floor(Math.random() * 21) + 80}%</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </motion.div>
            )}
          </AnimatePresence>
        </main>
      </div>
    </div>
  );
};

export default StudentDashboard;
