import React, { useState, useEffect } from 'react';
import { academicsService } from '../api/academicsService';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCreditCard, faCheckCircle, faFilter, faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';

interface Fee {
  fee_id: number;
  student: number;
  term: string;
  amount: string;
  paid_amount: string;
  status: 'Paid' | 'Unpaid' | 'Partial';
  due_date: string;
  paid_on?: string;
  balance: number;
  department_id: number;
  semester_id: number;
}

interface Student {
  id: number;
  first_name: string;
  last_name: string;
  registration_number: string;
  department_id: number;
  semester_id: number;
}

interface Department {
  id: number;
  name: string;
}

interface Semester {
  id: number;
  name: string;
  semester_code: string;
}

const FeeManagement: React.FC = () => {
  const [fees, setFees] = useState<Fee[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [semesters, setSemesters] = useState<Semester[]>([]);
  const [filteredFees, setFilteredFees] = useState<Fee[]>([]);
  const [selectedStudent, setSelectedStudent] = useState<number | null>(null);
  const [selectedDepartment, setSelectedDepartment] = useState<number | null>(null);
  const [selectedSemester, setSelectedSemester] = useState<number | null>(null);
  const [statusFilter, setStatusFilter] = useState<string>('All');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedFee, setSelectedFee] = useState<Fee | null>(null);
  const [paymentAmount, setPaymentAmount] = useState<string>('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fee amounts based on semester type
  const FEE_AMOUNTS = {
    even: 96000, // 96k for even semesters
    odd: 84000   // 84k for odd semesters
  };

  useEffect(() => {
    fetchDepartments();
    fetchStudents();
    fetchSemesters();
  }, []);

  useEffect(() => {
    if (selectedDepartment && selectedSemester) {
      generateFeesForStudents();
    }
  }, [selectedDepartment, selectedSemester, students]);

  useEffect(() => {
    filterFees();
  }, [fees, selectedStudent, statusFilter]);

  const fetchDepartments = async () => {
    try {
      const response = await academicsService.getDepartments();
      setDepartments(response.data);
    } catch (err) {
      console.error('Failed to fetch departments:', err);
    }
  };

  const fetchSemesters = async () => {
    try {
      const response = await academicsService.getSemesters();
      setSemesters(response.data);
    } catch (err) {
      console.error('Failed to fetch semesters:', err);
    }
  };

  const fetchStudents = async () => {
    try {
      const response = await academicsService.getStudents();
      setStudents(response.data);
    } catch (err) {
      console.error('Failed to fetch students:', err);
    }
  };

  const isEvenSemester = (semesterCode: string): boolean => {
    const semesterNumber = parseInt(semesterCode.replace(/\D/g, ''));
    return semesterNumber % 2 === 0;
  };

  const calculateFeeAmount = (semesterCode: string): number => {
    return isEvenSemester(semesterCode) ? FEE_AMOUNTS.even : FEE_AMOUNTS.odd;
  };

  const generateFeesForStudents = () => {
    if (!selectedDepartment || !selectedSemester) return;

    const selectedSem = semesters.find(s => s.id === selectedSemester);
    if (!selectedSem) return;

    const feeAmount = calculateFeeAmount(selectedSem.semester_code);
    const currentDate = new Date();
    const dueDate = new Date(currentDate.setMonth(currentDate.getMonth() + 1)).toISOString().split('T')[0];

    const newFees: Fee[] = students
      .filter(student => student.department_id === selectedDepartment)
      .map((student, index) => ({
        fee_id: Date.now() + index,
        student: student.id,
        term: `${selectedSem.name} - ${selectedSem.semester_code}`,
        amount: feeAmount.toString(),
        paid_amount: '0',
        status: 'Unpaid' as const,
        due_date: dueDate,
        balance: feeAmount,
        department_id: selectedDepartment,
        semester_id: selectedSemester
      }));

    setFees(newFees);
  };

  const filterFees = () => {
    let filtered = fees;
    if (selectedStudent) {
      filtered = filtered.filter(fee => fee.student === selectedStudent);
    }
    if (statusFilter !== 'All') {
      filtered = filtered.filter(fee => fee.status === statusFilter);
    }
    setFilteredFees(filtered);
  };

  const handlePayment = async () => {
    if (!selectedFee || !paymentAmount) return;

    setIsLoading(true);
    try {
      const updatedPaidAmount = parseFloat(selectedFee.paid_amount) + parseFloat(paymentAmount);
      const newStatus = updatedPaidAmount >= parseFloat(selectedFee.amount) ? 'Paid' : 'Partial';

      const updatedFee: Fee = {
        ...selectedFee,
        paid_amount: updatedPaidAmount.toString(),
        status: newStatus as 'Paid' | 'Unpaid' | 'Partial',
        paid_on: new Date().toISOString().split('T')[0],
      };

      await academicsService.updateFee(selectedFee.fee_id, updatedFee);
      setFees(fees.map(fee => fee.fee_id === selectedFee.fee_id ? updatedFee as Fee : fee));
      setIsModalOpen(false);
      setSelectedFee(null);
      setPaymentAmount('');
    } catch (err) {
      setError('Failed to update payment');
    } finally {
      setIsLoading(false);
    }
  };

  const openPaymentModal = (fee: Fee) => {
    setSelectedFee(fee);
    setPaymentAmount('');
    setIsModalOpen(true);
  };

  return (
    <div className="p-4 md:p-6 max-w-7xl mx-auto">
      <div className="flex items-center mb-6">
        <FontAwesomeIcon icon={faCreditCard} className="text-indigo-600 text-3xl mr-3" />
        <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Fee Management</h1>
      </div>

      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-4 rounded-r-lg flex items-center">
          <FontAwesomeIcon icon={faExclamationTriangle} className="text-red-500 mr-3" />
          <span className="text-red-700">{error}</span>
        </div>
      )}

      <div className="mb-6 bg-white p-4 rounded-lg shadow-sm">
        <div className="flex items-center mb-4">
          <FontAwesomeIcon icon={faFilter} className="text-gray-600 mr-2" />
          <h2 className="text-lg font-semibold text-gray-700">Filters</h2>
        </div>
        <div className="flex flex-col md:flex-row md:space-x-4 space-y-4 md:space-y-0">
          <div className="flex-1">
            <label className="block text-gray-700 text-sm font-bold mb-2">Filter by Student</label>
            <select
              value={selectedStudent || ''}
              onChange={(e) => setSelectedStudent(e.target.value ? parseInt(e.target.value) : null)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              <option value="">All Students</option>
              {students.map(student => (
                <option key={student.id} value={student.id}>
                  {student.first_name} {student.last_name} ({student.registration_number})
                </option>
              ))}
            </select>
          </div>

          <div className="flex-1">
            <label className="block text-gray-700 text-sm font-bold mb-2">Filter by Status</label>
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              <option value="All">All</option>
              <option value="Paid">Paid</option>
              <option value="Unpaid">Unpaid</option>
              <option value="Partial">Partial</option>
            </select>
          </div>
        </div>
      </div>

      {isLoading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-indigo-600"></div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-sm overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Term</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Paid Amount</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Balance</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Due Date</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredFees.map(fee => (
                  <tr key={fee.fee_id} className="hover:bg-gray-50 transition-colors duration-200">
                    <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-900">{fee.term}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-900">${fee.amount}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-900">${fee.paid_amount}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-900">${fee.balance.toFixed(2)}</td>
                    <td className="px-4 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        fee.status === 'Paid' ? 'bg-green-100 text-green-800' :
                        fee.status === 'Unpaid' ? 'bg-red-100 text-red-800' :
                        'bg-yellow-100 text-yellow-800'
                      }`}>
                        {fee.status === 'Paid' && <FontAwesomeIcon icon={faCheckCircle} className="mr-1" />}
                        {fee.status}
                      </span>
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-900">{fee.due_date}</td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm font-medium">
                      <button
                        onClick={() => openPaymentModal(fee)}
                        className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors duration-200"
                      >
                        <FontAwesomeIcon icon={faCreditCard} className="mr-2" />
                        Make Payment
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {isModalOpen && selectedFee && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-md mx-auto">
            <div className="p-6">
              <div className="flex items-center mb-4">
                <FaCreditCard className="text-indigo-600 text-xl mr-2" />
                <h2 className="text-xl font-bold text-gray-800">Make Payment</h2>
              </div>
              <div className="mb-4">
                <p className="text-gray-600">Fee for <span className="font-semibold">{selectedFee.term}</span></p>
                <p className="text-gray-600">Total Amount: <span className="font-semibold">${selectedFee.amount}</span></p>
                <p className="text-gray-600">Balance: <span className="font-semibold">${selectedFee.balance.toFixed(2)}</span></p>
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">Payment Amount</label>
                <input
                  type="number"
                  value={paymentAmount}
                  onChange={(e) => setPaymentAmount(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="Enter amount"
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setIsModalOpen(false)}
                  className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors duration-200"
                >
                  Cancel
                </button>
                <button
                  onClick={handlePayment}
                  disabled={isLoading || !paymentAmount}
                  className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 transition-colors duration-200"
                >
                  {isLoading ? <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div> : <FaCreditCard className="mr-2" />}
                  {isLoading ? 'Processing...' : 'Pay'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FeeManagement;
