import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { motion, AnimatePresence } from "framer-motion";
import { Bar, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

const StudentDashboard: React.FC = () => {
  const { currentUser, logout } = useAuth();
  const [activeTab, setActiveTab] = useState("dashboard");
  const [dashboardData, setDashboardData] = useState<any>(null);

  // âœ… Fetch Dashboard Data
  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        if (!currentUser?.id) return;
        const res = await axios.get(
          `http://127.0.0.1:8000/api/academics/dashboard/${currentUser.id}/`
        );
        setDashboardData(res.data);
      } catch (error) {
        console.error("Error fetching student dashboard:", error);
      }
    };
    fetchDashboard();
  }, [currentUser]);

  if (!dashboardData) {
    return (
      <div className="flex items-center justify-center h-screen text-lg font-semibold">
        Loading Dashboard...
      </div>
    );
  }

  const { student_info, attendance_summary, results, fees } = dashboardData;

  // ðŸŽ“ GPA / Attendance Summary Cards
  const stats = [
    { label: "Current GPA", value: student_info.gpa.toFixed(2) },
    { label: "Overall CGPA", value: student_info.cgpa.toFixed(2) },
    { label: "Attendance", value: `${attendance_summary.percentage}%` },
  ];

  // ðŸ“Š Results Chart (Grades)
  const resultChartData = useMemo(
    () => ({
      labels: results.map((r: any) => r.course_name),
      datasets: [
        {
          label: "Grades %",
          data: results.map((r: any) => r.obtained_marks || 0),
          backgroundColor: "rgba(99, 102, 241, 0.6)",
        },
      ],
    }),
    [results]
  );

  // ðŸ“ˆ Attendance Chart
  const attendanceChartData = useMemo(
    () => ({
      labels: ["Total Classes", "Present Classes"],
      datasets: [
        {
          label: "Attendance",
          data: [attendance_summary.total_classes, attendance_summary.present_classes],
          backgroundColor: ["rgba(54,162,235,0.6)", "rgba(75,192,192,0.6)"],
        },
      ],
    }),
    [attendance_summary]
  );

  const chartOptions = { responsive: true, maintainAspectRatio: false };

  const tabs = [
    { id: "dashboard", label: "Dashboard" },
    { id: "results", label: "Results" },
    { id: "fees", label: "Fees" },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-indigo-800 text-white p-4 min-h-screen">
        <div className="text-center mb-6">
          <div className="w-20 h-20 bg-white text-indigo-700 rounded-full flex items-center justify-center mx-auto mb-2 text-2xl font-bold">
            {student_info.name[0]}
          </div>
          <h3 className="text-lg font-semibold">{student_info.name}</h3>
          <p className="text-sm text-indigo-200">{student_info.department}</p>
        </div>

        <ul className="space-y-2">
          {tabs.map((tab) => (
            <li key={tab.id}>
              <button
                onClick={() => setActiveTab(tab.id)}
                className={`w-full text-left px-4 py-2 rounded-lg transition ${
                  activeTab === tab.id ? "bg-indigo-600" : "hover:bg-indigo-700"
                }`}
              >
                {tab.label}
              </button>
            </li>
          ))}
        </ul>

        <button
          onClick={logout}
          className="mt-8 w-full bg-red-600 hover:bg-red-700 py-2 rounded-lg"
        >
          Logout
        </button>
      </div>

      {/* Main Section */}
      <div className="flex-1 overflow-auto">
        <header className="bg-gradient-to-r from-indigo-600 to-purple-600 px-6 py-4 text-white flex justify-between items-center">
          <h1 className="text-xl font-semibold">
            {tabs.find((t) => t.id === activeTab)?.label}
          </h1>
        </header>

        <main className="p-6">
          <AnimatePresence mode="wait">
            {/* Dashboard */}
            {activeTab === "dashboard" && (
              <motion.div
                key="dashboard"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                {/* Cards */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                  {stats.map((s, i) => (
                    <div key={i} className="bg-white p-6 rounded-xl shadow text-center">
                      <h3 className="text-sm text-gray-500">{s.label}</h3>
                      <p className="text-2xl font-bold text-indigo-700 mt-2">{s.value}</p>
                    </div>
                  ))}
                </div>

                {/* Charts */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div className="bg-white p-4 rounded-xl shadow">
                    <h3 className="text-lg font-semibold mb-2">Grades Overview</h3>
                    <div className="h-56">
                      <Bar data={resultChartData} options={chartOptions} />
                    </div>
                  </div>
                  <div className="bg-white p-4 rounded-xl shadow">
                    <h3 className="text-lg font-semibold mb-2">Attendance Summary</h3>
                    <div className="h-56">
                      <Bar data={attendanceChartData} options={chartOptions} />
                    </div>
                  </div>
                </div>
              </motion.div>
            )}

            {/* Results */}
            {activeTab === "results" && (
              <motion.div key="results" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                <h2 className="text-xl font-semibold mb-4">My Results</h2>
                <table className="min-w-full bg-white rounded-xl shadow">
                  <thead>
                    <tr className="bg-indigo-100 text-gray-800">
                      <th className="py-2 px-4">Course</th>
                      <th className="py-2 px-4">Grade</th>
                      <th className="py-2 px-4">GPA</th>
                      <th className="py-2 px-4">Marks</th>
                    </tr>
                  </thead>
                  <tbody>
                    {results.map((r: any, idx: number) => (
                      <tr key={idx} className="border-t text-center">
                        <td className="py-2 px-4">{r.course_name}</td>
                        <td className="py-2 px-4">{r.grade}</td>
                        <td className="py-2 px-4">{r.gpa}</td>
                        <td className="py-2 px-4">{r.obtained_marks}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </motion.div>
            )}

            {/* Fees */}
            {activeTab === "fees" && (
              <motion.div key="fees" initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
                <h2 className="text-xl font-semibold mb-4">My Fees</h2>
                <table className="min-w-full bg-white rounded-xl shadow">
                  <thead>
                    <tr className="bg-indigo-100 text-gray-800">
                      <th className="py-2 px-4">Term</th>
                      <th className="py-2 px-4">Amount</th>
                      <th className="py-2 px-4">Paid</th>
                      <th className="py-2 px-4">Status</th>
                      <th className="py-2 px-4">Due Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {fees.map((f: any, idx: number) => (
                      <tr key={idx} className="border-t text-center">
                        <td className="py-2 px-4">{f.term}</td>
                        <td className="py-2 px-4">{f.amount}</td>
                        <td className="py-2 px-4">{f.paid_amount}</td>
                        <td className="py-2 px-4">{f.status}</td>
                        <td className="py-2 px-4">{f.due_date}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </motion.div>
            )}
          </AnimatePresence>
        </main>
      </div>
    </div>
  );
};

export default StudentDashboard;
