import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const StudentDashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  const studentId = "gen002"; // ðŸ”— later connect from AuthContext

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const response = await axios.get(
          `http://127.0.0.1:8000/api/academics/dashboard/${studentId}/`
        );
        setDashboardData(response.data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [studentId]);

  const resultChartData = useMemo(() => {
    const results = dashboardData?.results || [];
    return {
      labels: results.map((r: any) => r.course_name || "N/A"),
      datasets: [
        {
          label: "Obtained Marks",
          data: results.map((r: any) => r.obtained_marks || 0),
          backgroundColor: results.map(() =>
            "rgba(99, 102, 241, 0.7)"
          ),
          borderRadius: 8,
        },
      ],
    };
  }, [dashboardData]);

  const resultChartOptions = {
    plugins: {
      legend: { display: false },
      tooltip: {
        backgroundColor: "#111827",
        titleColor: "#fff",
        bodyColor: "#d1d5db",
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: { color: "#6b7280", stepSize: 10 },
        grid: { color: "#f3f4f6" },
      },
      x: {
        ticks: { color: "#6b7280" },
        grid: { display: false },
      },
    },
  };

  const attendanceChartData = useMemo(() => {
    const a = dashboardData?.attendance_summary || {
      total_classes: 0,
      present_classes: 0,
    };
    return {
      labels: ["Present", "Absent"],
      datasets: [
        {
          label: "Attendance",
          data: [
            a.present_classes,
            a.total_classes - a.present_classes,
          ],
          backgroundColor: ["#4F46E5", "#E5E7EB"],
          borderWidth: 0,
        },
      ],
    };
  }, [dashboardData]);

  const attendanceOptions = {
    cutout: "70%",
    plugins: {
      legend: {
        position: "bottom" as const,
        labels: { color: "#4b5563", boxWidth: 15 },
      },
    },
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-indigo-600">
        Loading your dashboard...
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="flex items-center justify-center h-screen text-xl font-semibold text-red-600">
        Failed to load dashboard data.
      </div>
    );
  }

  const student = dashboardData.student_info;
  const attendance = dashboardData.attendance_summary;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-white p-8">
      {/* Header */}
      <div className="text-center mb-10">
        <h1 className="text-4xl font-extrabold text-indigo-700 drop-shadow-md">
          ðŸŽ“ Student Dashboard
        </h1>
        <p className="text-gray-600 mt-2 text-lg">
          Welcome back,{" "}
          <span className="font-semibold text-indigo-600">
            {student?.name || "Student"}
          </span>
        </p>
      </div>

      {/* Info Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-6">
        {[
          { title: "Name", value: student?.name },
          { title: "Email", value: student?.email },
          { title: "Department", value: student?.department },
          { title: "Semester", value: student?.semester },
          { title: "GPA", value: student?.gpa },
          { title: "CGPA", value: student?.cgpa },
        ].map((item, i) => (
          <div
            key={i}
            className="bg-white shadow-lg rounded-2xl p-4 hover:shadow-2xl transition-shadow border-t-4 border-indigo-600"
          >
            <h2 className="font-semibold text-gray-600 text-sm uppercase tracking-wide">
              {item.title}
            </h2>
            <p className="text-lg font-bold text-indigo-700 mt-1">{item.value}</p>
          </div>
        ))}
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-10 mt-12">
        {/* Attendance */}
        <div className="bg-white p-6 shadow-lg rounded-2xl hover:shadow-2xl transition">
          <h2 className="text-lg font-bold text-gray-700 mb-6 flex items-center gap-2">
            ðŸ“Š Attendance Summary
          </h2>
          <div className="w-64 mx-auto">
            <Doughnut data={attendanceChartData} options={attendanceOptions} />
          </div>
          <p className="text-center mt-6 text-gray-600 font-medium">
            Attendance:{" "}
            <span className="text-indigo-700 font-bold">
              {attendance?.percentage || 0}%
            </span>
          </p>
        </div>

        {/* Results */}
        <div className="bg-white p-6 shadow-lg rounded-2xl hover:shadow-2xl transition">
          <h2 className="text-lg font-bold text-gray-700 mb-6 flex items-center gap-2">
            ðŸ§¾ Results Overview
          </h2>
          {dashboardData.results.length === 0 ? (
            <p className="text-center text-gray-500 mt-20">
              No results uploaded yet.
            </p>
          ) : (
            <Bar data={resultChartData} options={resultChartOptions} />
          )}
        </div>
      </div>

      {/* Fees Section */}
      <div className="bg-white p-6 shadow-lg rounded-2xl mt-12 hover:shadow-2xl transition">
        <h2 className="text-lg font-bold text-gray-700 mb-6 flex items-center gap-2">
          ðŸ’° Fee Records
        </h2>

        {dashboardData.fees.length === 0 ? (
          <p className="text-gray-500 text-center">No fee records available.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full border border-gray-200 rounded-lg text-sm">
              <thead className="bg-indigo-100 text-gray-700">
                <tr>
                  <th className="p-3 border">Description</th>
                  <th className="p-3 border">Amount</th>
                  <th className="p-3 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {dashboardData.fees.map((f: any, idx: number) => (
                  <tr
                    key={idx}
                    className="text-center hover:bg-gray-50 transition-colors"
                  >
                    <td className="p-3 border">{f.description}</td>
                    <td className="p-3 border font-medium text-gray-800">
                      Rs. {f.amount}
                    </td>
                    <td
                      className={`p-3 border font-semibold ${
                        f.status === "Paid"
                          ? "text-green-600"
                          : "text-red-600"
                      }`}
                    >
                      {f.status}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
