import React, { useEffect, useState } from "react";
import { libraryService } from "../../api/libraryService";
import BookCard from "../library/BookCard";
import AddBookModal from "../library/AddBookModel";
import { Book } from "../../models/library";
import { useAuth } from "../../context/AuthContext";

const LibraryManagement: React.FC = () => {
  const { currentUser } = useAuth();
  const isAdmin = currentUser?.role === "admin";
  const [books, setBooks] = useState<Book[]>([]);
  const [loading, setLoading] = useState(false);
  const [showAdd, setShowAdd] = useState(false);
  const [editingBook, setEditingBook] = useState<Book | null>(null);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const res = await libraryService.getBooks();
      setBooks(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to load books");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchBooks(); }, []);

  const handleBorrow = async (id: number) => {
    try {
      await libraryService.borrowBook(id);
      fetchBooks();
      alert("Book borrowed");
    } catch (err) {
      console.error(err);
      alert("Borrow failed");
    }
  };

  const handleReturn = async (id: number) => {
    try {
      await libraryService.returnBook(id);
      fetchBooks();
      alert("Returned");
    } catch (err) {
      console.error(err);
      alert("Return failed");
    }
  };

  const handleDelete = async (id: number) => {
    if (!confirm("Delete this book?")) return;
    try {
      await libraryService.deleteBook(id);
      fetchBooks();
    } catch (err) {
      console.error(err);
      alert("Delete failed");
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Library</h2>
        {isAdmin && <button onClick={()=>{ setEditingBook(null); setShowAdd(true); }} className="bg-green-600 text-white px-3 py-1 rounded">Add Book</button>}
      </div>

      {loading ? <p>Loading...</p> : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {books.map(b => (
            <BookCard
              key={b.id}
              book={b}
              onBorrow={handleBorrow}
              onReturn={handleReturn}
              onEdit={(bk) => { setEditingBook(bk); setShowAdd(true); }}
              onDelete={handleDelete}
              isAdmin={isAdmin}
            />
          ))}
        </div>
      )}

      {showAdd && (
        <AddBookModal
          isOpen={showAdd}
          initial={editingBook || undefined}
          onClose={() => setShowAdd(false)}
          onSaved={() => fetchBooks()}
        />
      )}
    </div>
  );
};

export default LibraryManagement;
