import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Bar } from "react-chartjs-2";
import {
  GraduationCap,
  User,
  ClipboardList,
  MessageSquare,
  CalendarDays,
  Bell,
  BookOpen,
  LogOut,
  Camera,
  Mail,
  Phone,
  IdCard,
  Layers,
} from "lucide-react";
import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:8000/api";

const StudentDashboard: React.FC = () => {
  const [studentData, setStudentData] = useState<any>(null);
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [uploading, setUploading] = useState<boolean>(false);

  const studentId = "gen002"; // Example only

  useEffect(() => {
    fetchStudentData();
  }, []);

  const fetchStudentData = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/students/${studentId}/`);
      setStudentData(response.data);
    } catch (error) {
      console.error("Error fetching student data:", error);
    }
  };

  const handleImageUpload = async () => {
    if (!selectedImage) return alert("Please select an image first!");
    setUploading(true);
    const formData = new FormData();
    formData.append("image", selectedImage);

    try {
      const response = await axios.patch(
        `${API_BASE_URL}/students/${studentId}/upload-image/`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setStudentData({ ...studentData, image: response.data.image });
      alert("Profile image updated successfully!");
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Error uploading image!");
    } finally {
      setUploading(false);
    }
  };

  const chartData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
    datasets: [
      {
        label: "Attendance %",
        data: [85, 90, 88, 92],
        backgroundColor: "rgba(37, 99, 235, 0.6)",
      },
    ],
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-xl flex flex-col justify-between">
        <div>
          <div className="p-6 flex items-center gap-3 border-b">
            <GraduationCap className="text-blue-600" size={26} />
            <h2 className="font-extrabold text-xl text-gray-800 tracking-tight">
              Student Portal
            </h2>
          </div>

          <nav className="p-5 flex flex-col gap-5 text-gray-700 font-medium">
            <button className="flex items-center gap-3 hover:text-blue-600">
              <ClipboardList size={18} /> Results
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <Layers size={18} /> Attendance
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <MessageSquare size={18} /> Messaging
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <CalendarDays size={18} /> Events
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <Bell size={18} /> Announcements
            </button>
            <button className="flex items-center gap-3 hover:text-blue-600">
              <BookOpen size={18} /> Library
            </button>
          </nav>
        </div>

        {/* Logout button */}
        <div className="p-5 border-t flex items-center gap-2 hover:text-red-600 cursor-pointer">
          <LogOut size={18} /> Logout
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8">
        {/* Header */}
        <motion.div
          className="flex justify-between items-center mb-8"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <h1 className="text-3xl font-bold text-blue-600">
            Welcome, {studentData?.name || "Student"} 👋
          </h1>
          <p className="text-gray-500 text-sm">
            {new Date().toLocaleDateString("en-GB", {
              weekday: "long",
              day: "numeric",
              month: "short",
              year: "numeric",
            })}
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Profile Section */}
          <motion.div
            className="bg-white rounded-2xl shadow-md p-6 flex flex-col items-center text-center"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <div className="relative">
              <img
                src={
                  studentData?.image
                    ? `${studentData.image}`
                    : "https://via.placeholder.com/120"
                }
                alt="Profile"
                className="w-28 h-28 rounded-full object-cover border-4 border-blue-500"
              />
              <label className="absolute bottom-0 right-0 bg-blue-500 p-2 rounded-full cursor-pointer">
                <Camera size={18} color="white" />
                <input
                  type="file"
                  hidden
                  onChange={(e) =>
                    setSelectedImage(e.target.files ? e.target.files[0] : null)
                  }
                />
              </label>
            </div>

            <h2 className="font-semibold text-lg mt-4">{studentData?.name}</h2>
            <p className="text-gray-500 text-sm">{studentData?.email}</p>
            <button
              onClick={handleImageUpload}
              disabled={uploading}
              className="mt-3 bg-blue-600 text-white px-4 py-1 rounded-lg hover:bg-blue-700"
            >
              {uploading ? "Uploading..." : "Upload Image"}
            </button>
          </motion.div>

          {/* Attendance Chart */}
          <motion.div
            className="bg-white rounded-2xl shadow-md p-6 col-span-2"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <h3 className="text-lg font-semibold mb-4 text-gray-700">
              Weekly Attendance Overview
            </h3>
            <Bar data={chartData} />
          </motion.div>

          {/* Profile Info Card */}
          <motion.div
            className="bg-white rounded-2xl shadow-md p-6"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <h3 className="text-lg font-semibold mb-4 text-gray-700 border-b pb-2">
              Profile Details
            </h3>
            <ul className="space-y-3 text-gray-700">
              <li className="flex items-center gap-3">
                <IdCard size={18} className="text-blue-600" />
                <span>ID: {studentData?.student_id}</span>
              </li>
              <li className="flex items-center gap-3">
                <Layers size={18} className="text-blue-600" />
                <span>Department: {studentData?.department_name || "—"}</span>
              </li>
              <li className="flex items-center gap-3">
                <ClipboardList size={18} className="text-blue-600" />
                <span>Semester: {studentData?.semester_name || "—"}</span>
              </li>
              <li className="flex items-center gap-3">
                <Mail size={18} className="text-blue-600" />
                <span>{studentData?.email}</span>
              </li>
              <li className="flex items-center gap-3">
                <Phone size={18} className="text-blue-600" />
                <span>{studentData?.phone}</span>
              </li>
            </ul>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
