import React from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

interface Course {
  course_id: number;
  name: string;
  code: string;
  credits: number;
}

interface Result {
  id: number;
  subject: string;
  marks: number;
  grade: string;
  remarks: string;
  is_pending: boolean;
}

interface Student {
  id: number;
  name: string;
  email: string;
  student_id: string;
  enrollment_date: string;
  department: { name: string };
  semester: { name: string };
  image?: string;
}

interface ReportCardProps {
  student: Student;
  assignedCourses: Course[];
  results: Result[];
  onClose: () => void;
}

const ReportCard: React.FC<ReportCardProps> = ({ student, assignedCourses, results, onClose }) => {
  // Calculate GPA and other stats
  const calculateGPA = () => {
    const completedResults = results.filter(r => !r.is_pending);
    if (completedResults.length === 0) return 0;

    const gradePoints: { [key: string]: number } = {
      'A+': 4.0, 'A': 4.0, 'A-': 3.7,
      'B+': 3.3, 'B': 3.0, 'B-': 2.7,
      'C+': 2.3, 'C': 2.0, 'C-': 1.7,
      'D+': 1.3, 'D': 1.0, 'F': 0.0
    };

    const totalPoints = completedResults.reduce((sum, result) => {
      return sum + (gradePoints[result.grade] || 0);
    }, 0);

    return totalPoints / completedResults.length;
  };

  const gpa = calculateGPA();
  const totalCredits = assignedCourses.reduce((sum, course) => sum + course.credits, 0);
  const completedCourses = results.filter(r => !r.is_pending).length;

  // Get result for a course
  const getCourseResult = (courseName: string) => {
    return results.find(result => result.subject === courseName);
  };

  // Get grade color
  const getGradeColor = (grade: string, isPending: boolean) => {
    if (isPending) return 'bg-gradient-to-r from-gray-100 to-gray-200 text-gray-600 border-gray-300';
    if (grade.startsWith('A')) return 'bg-gradient-to-r from-green-100 to-green-200 text-green-800 border-green-300';
    if (grade.startsWith('B')) return 'bg-gradient-to-r from-blue-100 to-blue-200 text-blue-800 border-blue-300';
    if (grade.startsWith('C')) return 'bg-gradient-to-r from-yellow-100 to-yellow-200 text-yellow-800 border-yellow-300';
    if (grade.startsWith('D')) return 'bg-gradient-to-r from-orange-100 to-orange-200 text-orange-800 border-orange-300';
    return 'bg-gradient-to-r from-red-100 to-red-200 text-red-800 border-red-300';
  };

  const handlePrint = () => {
    window.print();
  };

  const handleDownloadPDF = async () => {
    const element = document.getElementById('report-card-content');
    if (!element) return;

    try {
      const canvas = await html2canvas(element, {
        scale: 2,
        useCORS: true,
        allowTaint: true,
        backgroundColor: '#ffffff',
        width: element.scrollWidth,
        height: element.scrollHeight,
      });

      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');

      const imgWidth = 210; // A4 width in mm
      const pageHeight = 295; // A4 height in mm
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      let heightLeft = imgHeight;

      let position = 0;

      // Add first page
      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      // Add additional pages if needed
      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }

      pdf.save(`${student.name}_Report_Card.pdf`);
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Failed to generate PDF. Please try again.');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-2xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header with actions */}
        <div className="flex justify-between items-center p-6 border-b bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-t-lg print:hidden">
          <h2 className="text-2xl font-bold">Student Report Card</h2>
          <div className="flex space-x-2">
            <button
              onClick={handlePrint}
              className="bg-white text-blue-600 px-4 py-2 rounded hover:bg-gray-100 flex items-center space-x-2"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 17h2a2 2 0 002-2v-4a2 2 0 00-2-2H5a2 2 0 00-2 2v4a2 2 0 002 2h2m2 4h6a2 2 0 002-2v-4a2 2 0 00-2-2H9a2 2 0 00-2 2v4a2 2 0 002 2zm8-12V5a2 2 0 00-2-2H9a2 2 0 00-2 2v4h10z" />
              </svg>
              <span>Print</span>
            </button>
            <button
              onClick={onClose}
              className="bg-white text-blue-600 px-4 py-2 rounded hover:bg-gray-100"
            >
              Close
            </button>
          </div>
        </div>

        {/* Report Card Content */}
        <div className="p-8 print:p-6" id="report-card-content">
          {/* Header */}
          <div className="text-center mb-8 border-b-2 border-blue-600 pb-4">
            <h1 className="text-3xl font-bold text-blue-600 mb-2">University Management Institute</h1>
            <h2 className="text-xl font-semibold text-gray-700">Academic Report Card</h2>
            <p className="text-gray-600">Semester: {student.semester?.name || 'N/A'}</p>
          </div>

          {/* Student Information */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <div className="md:col-span-1">
              {student.image ? (
                <img
                  src={student.image}
                  alt={student.name}
                  className="w-32 h-32 object-cover rounded-lg border-2 border-gray-300 mx-auto"
                />
              ) : (
                <div className="w-32 h-32 bg-gray-200 rounded-lg flex items-center justify-center mx-auto">
                  <span className="text-2xl text-gray-500">{student.name?.charAt(0) || 'S'}</span>
                </div>
              )}
            </div>
            <div className="md:col-span-2">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-600">Student Name</label>
                  <p className="text-lg font-semibold">{student.name}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-600">Student ID</label>
                  <p className="text-lg font-semibold">{student.student_id}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-600">Department</label>
                  <p className="text-lg">{student.department?.name || 'N/A'}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-600">Enrollment Date</label>
                  <p className="text-lg">{new Date(student.enrollment_date).toLocaleDateString()}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-600">Email</label>
                  <p className="text-lg">{student.email}</p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-600">GPA</label>
                  <p className="text-lg font-bold text-blue-600">{gpa.toFixed(2)}</p>
                </div>
              </div>
            </div>
          </div>

          {/* Course Results Table */}
          <div className="mb-8">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Course Results</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full border border-gray-300">
                <thead>
                  <tr className="bg-blue-50">
                    <th className="border border-gray-300 px-4 py-3 text-left text-sm font-semibold text-gray-700">Course Code</th>
                    <th className="border border-gray-300 px-4 py-3 text-left text-sm font-semibold text-gray-700">Course Name</th>
                    <th className="border border-gray-300 px-4 py-3 text-center text-sm font-semibold text-gray-700">Credits</th>
                    <th className="border border-gray-300 px-4 py-3 text-center text-sm font-semibold text-gray-700">Marks</th>
                    <th className="border border-gray-300 px-4 py-3 text-center text-sm font-semibold text-gray-700">Grade</th>
                    <th className="border border-gray-300 px-4 py-3 text-left text-sm font-semibold text-gray-700">Status</th>
                  </tr>
                </thead>
                <tbody>
                  {assignedCourses.map((course) => {
                    const result = getCourseResult(course.name);
                    const isPending = !result || result.is_pending;
                    const marks = result ? result.marks : 'N/A';
                    const grade = result && !result.is_pending ? result.grade : 'Pending';
                    const status = isPending ? 'Pending' : 'Completed';

                    return (
                      <tr key={course.course_id} className="hover:bg-gray-50">
                        <td className="border border-gray-300 px-4 py-3 text-sm font-medium">{course.code}</td>
                        <td className="border border-gray-300 px-4 py-3 text-sm">{course.name}</td>
                        <td className="border border-gray-300 px-4 py-3 text-center text-sm">{course.credits}</td>
                        <td className="border border-gray-300 px-4 py-3 text-center text-sm">{marks}</td>
                        <td className="border border-gray-300 px-4 py-3 text-center">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getGradeColor(grade, isPending)}`}>
                            {grade}
                          </span>
                        </td>
                        <td className="border border-gray-300 px-4 py-3 text-sm">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                            status === 'Completed' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {status}
                          </span>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>

          {/* Summary */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <div className="bg-blue-50 p-4 rounded-lg border">
              <h4 className="font-semibold text-blue-800 mb-2">Total Courses</h4>
              <p className="text-2xl font-bold text-blue-600">{assignedCourses.length}</p>
            </div>
            <div className="bg-green-50 p-4 rounded-lg border">
              <h4 className="font-semibold text-green-800 mb-2">Completed Courses</h4>
              <p className="text-2xl font-bold text-green-600">{completedCourses}</p>
            </div>
            <div className="bg-purple-50 p-4 rounded-lg border">
              <h4 className="font-semibold text-purple-800 mb-2">Total Credits</h4>
              <p className="text-2xl font-bold text-purple-600">{totalCredits}</p>
            </div>
          </div>

          {/* Performance Remarks */}
          <div className="mb-8">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Performance Remarks</h3>
            <div className="bg-gray-50 p-4 rounded-lg border">
              {gpa >= 3.5 ? (
                <p className="text-green-800">Excellent performance! Keep up the great work.</p>
              ) : gpa >= 3.0 ? (
                <p className="text-blue-800">Good performance. Continue to strive for excellence.</p>
              ) : gpa >= 2.0 ? (
                <p className="text-yellow-800">Satisfactory performance. Focus on improvement areas.</p>
              ) : (
                <p className="text-red-800">Performance needs improvement. Seek academic support if needed.</p>
              )}
            </div>
          </div>

          {/* Signatures */}
          <div className="grid grid-cols-2 gap-8 mt-12">
            <div className="text-center">
              <div className="border-t border-gray-400 pt-2">
                <p className="font-semibold">Department Head</p>
                <p className="text-sm text-gray-600">Signature & Date</p>
              </div>
            </div>
            <div className="text-center">
              <div className="border-t border-gray-400 pt-2">
                <p className="font-semibold">Academic Advisor</p>
                <p className="text-sm text-gray-600">Signature & Date</p>
              </div>
            </div>
          </div>

          {/* Footer */}
          <div className="text-center mt-8 text-sm text-gray-600 border-t pt-4">
            <p>This is an official academic record of the University Management Institute.</p>
            <p>Generated on {new Date().toLocaleDateString()}</p>
          </div>
        </div>
      </div>

      {/* Print Styles */}
      <style>{`
        @media print {
          .print\\:hidden {
            display: none !important;
          }
          #report-card-content {
            -webkit-print-color-adjust: exact;
            color-adjust: exact;
          }
          body {
            print-color-adjust: exact;
          }
        }
      `}</style>
    </div>
  );
};

export default ReportCard;
