sourcesContent:
/**
 * Professional Admin Dashboard Component
 * Modern administrative interface for university management system with comprehensive statistics
 */
import { useAuth } from '../context/AuthContext';
import { useState, useEffect, useMemo, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Bar, Line, Pie, Doughnut } from 'react-chartjs-2';
import { messagingService, departmentService, courseService, studentService } from '../api/apiService';
import { instructorService } from '../api/studentInstructorService';
import { academicsService } from '../api/academicsService_enhanced';
import StudentManagement from '../components/pages/StudentManagement';
import DepartmentManagement from '../components/pages/DepartmentManagement';
import CourseManagement from '../components/pages/CourseManagement';
import TeacherManagement from '../components/pages/TeacherManagement';
import MessagingSystem from '../components/messaging/MessagingSystem';
import { jsPDF } from 'jspdf';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

// TypeScript interfaces for data structures
interface Department {
  id: number;
  name?: string;
  department_name?: string;
  title?: string;
  code?: string;
  description?: string;
  num_semesters?: number;
}

interface Student {
  id: number;
  name?: string;
  student_id?: string | number;
  first_name?: string;
  last_name?: string;
  department?: Department | number;
  semester?: any;
  gpa?: number;
  attendance_percentage?: number;
  enrollment_date?: string;
}

interface Instructor {
  id: number;
  name?: string;
  employee_id?: string;
  department?: Department | number;
  designation?: string;
}

interface Course {
  id: number;
  name?: string;
  code?: string;
  department?: Department | number;
  credits?: number;
}

interface DashboardStats {
  totalStudents: number;
  totalInstructors: number;
  totalDepartments: number;
  totalCourses: number;
  activeStudents: number;
  averageGPA: number;
  averageAttendance: number;
  totalCapacity: number;
}

interface DepartmentStats {
  department: Department;
  studentCount: number;
  courseCount: number;
  averageGPA: number;
  attendanceRate: number;
}

const AdminDashboard = () => {
  const { currentUser, logout } = useAuth();
  const [activeTab, setActiveTab] = useState<string>('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState<DashboardStats>({
    totalStudents: 0,
    totalInstructors: 0,
    totalDepartments: 0,
    totalCourses: 0,
    activeStudents: 0,
    averageGPA: 0,
    averageAttendance: 0,
    totalCapacity: 0,
  });

  const [departmentStats, setDepartmentStats] = useState<DepartmentStats[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [instructors, setInstructors] = useState<Instructor[]>([]);
  const [courses, setCourses] = useState<Course[]>([]);

  // Fetch comprehensive dashboard data
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);

        // Fetch all required data in parallel
        const [studentsRes, departmentsRes, coursesRes, instructorsRes] = await Promise.all([
          studentService.getAllStudents(),
          departmentService.getAllDepartments(),
          courseService.getAllCourses(),
          instructorService.getAllInstructors(),
        ]);

        const studentsData = studentsRes.data || [];
        const departmentsData = departmentsRes.data || [];
        const coursesData = coursesRes.data || [];
        const instructorsData = instructorsRes.data || [];

        // Calculate comprehensive statistics
        const totalStudents = studentsData.length;
        const totalInstructors = instructorsData.length;
        const totalDepartments = departmentsData.length;
        const totalCourses = coursesData.length;

        // Calculate active students (assuming students with recent activity)
        const activeStudents = Math.floor(totalStudents * 0.85);

        // Calculate average GPA
        const studentsWithGPA = studentsData.filter(s => s.gpa && s.gpa > 0);
        const averageGPA = studentsWithGPA.length > 0
          ? studentsWithGPA.reduce((sum, s) => sum + (s.gpa || 0), 0) / studentsWithGPA.length
          : 0;

        // Calculate average attendance
        const studentsWithAttendance = studentsData.filter(s => s.attendance_percentage);
        const averageAttendance = studentsWithAttendance.length > 0
          ? studentsWithAttendance.reduce((sum, s) => sum + (s.attendance_percentage || 0), 0) / studentsWithAttendance.length
          : 0;

        // Calculate total capacity
        const totalCapacity = departmentsData.reduce((sum, d) => sum + (d.num_semesters || 8) * 30, 0);

        setStats({
          totalStudents,
          totalInstructors,
          totalDepartments,
          totalCourses,
          activeStudents,
          averageGPA: Math.round(averageGPA * 100) / 100,
          averageAttendance: Math.round(averageAttendance * 100) / 100,
          totalCapacity,
        });

        setStudents(studentsData);
        setDepartments(departmentsData);
        setCourses(coursesData);
        setInstructors(instructorsData);

        // Calculate department-wise statistics
        const deptStats: DepartmentStats[] = departmentsData.map(dept => {
          const deptStudents = studentsData.filter(s =>
            (typeof s.department === 'object' && s.department?.id === dept.id) ||
            s.department === dept.id
          );

          const deptCourses = coursesData.filter(c =>
            (typeof c.department === 'object' && c.department?.id === dept.id) ||
            c.department === dept.id
          );

          const deptAverageGPA = deptStudents.length > 0
            ? deptStudents.reduce((sum, s) => sum + (s.gpa || 0), 0) / deptStudents.length
            : 0;

          const deptAttendanceRate = deptStudents.length > 0
            ? deptStudents.reduce((sum, s) => sum + (s.attendance_percentage || 0), 0) / deptStudents.length
            : 0;

          return {
            department: dept,
            studentCount: deptStudents.length,
            courseCount: deptCourses.length,
            averageGPA: Math.round(deptAverageGPA * 100) / 100,
            attendanceRate: Math.round(deptAttendanceRate * 100) / 100,
          };
        });

        setDepartmentStats(deptStats);

      } catch (error) {
        console.error('Failed to fetch dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  // Chart data for department distribution
  const departmentChartData = useMemo(() => {
    return {
      labels: departmentStats.map(ds => ds.department.name || ds.department.department_name || 'Unknown'),
      datasets: [
        {
          label: 'Students',
          data: departmentStats.map(ds => ds.studentCount),
          backgroundColor: [
            'rgba(59, 130, 246, 0.8)',
            'rgba(16, 185, 129, 0.8)',
            'rgba(245, 158, 11, 0.8)',
            'rgba(239, 68, 68, 0.8)',
            'rgba(139, 92, 246, 0.8)',
            'rgba(236, 72, 153, 0.8)',
          ],
          borderColor: [
            'rgba(59, 130, 246, 1)',
            'rgba(16, 185, 129, 1)',
            'rgba(245, 158, 11, 1)',
            'rgba(239, 68, 68, 1)',
            'rgba(139, 92, 246, 1)',
            'rgba(236, 72, 153, 1)',
          ],
          borderWidth: 2,
        },
      ],
    };
  }, [departmentStats]);

  // Chart data for performance trends
  const performanceChartData = useMemo(() => {
    return {
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets: [
        {
          label: 'Average GPA',
          data: [3.2, 3.4, 3.3, 3.5, 3.6, 3.7],
          borderColor: 'rgba(59, 130, 246, 1)',
          backgroundColor: 'rgba(59, 130, 246, 0.1)',
          tension: 0.4,
        },
        {
          label: 'Attendance Rate (%)',
          data: [85, 88, 82, 90, 92, 89],
          borderColor: 'rgba(16, 185, 129, 1)',
          backgroundColor: 'rgba(16, 185, 129, 0.1)',
          tension: 0.4,
        },
      ],
    };
  }, []);

  // Chart data for GPA distribution
  const gpaDistributionData = useMemo(() => {
    const gpaRanges = {
      '4.0': 0,
      '3.5-3.9': 0,
      '3.0-3.4': 0,
      '2.5-2.9': 0,
      '2.0-2.4': 0,
      'Below 2.0': 0,
    };

    students.forEach(student => {
      const gpa = student.gpa || 0;
      if (gpa >= 4.0) gpaRanges['4.0']++;
      else if (gpa >= 3.5) gpaRanges['3.5-3.9']++;
      else if (gpa >= 3.0) gpaRanges['3.0-3.4']++;
      else if (gpa >= 2.5) gpaRanges['2.5-2.9']++;
      else if (gpa >= 2.0) gpaRanges['2.0-2.4']++;
      else gpaRanges['Below 2.0']++;
    });

    return {
      labels: Object.keys(gpaRanges),
      datasets: [
        {
          data: Object.values(gpaRanges),
          backgroundColor: [
            'rgba(16, 185, 129, 0.8)',
            'rgba(59, 130, 246, 0.8)',
            'rgba(245, 158, 11, 0.8)',
            'rgba(251, 146, 60, 0.8)',
            'rgba(239, 68, 68, 0.8)',
            'rgba(139, 69, 19, 0.8)',
          ],
          borderWidth: 2,
        },
      ],
    };
  }, [students]);

  // Statistics Cards Component
  const StatisticsCard = ({
    title,
    value,
    icon,
    color,
    subtitle,
    trend
  }: {
    title: string;
    value: string | number;
    icon: React.ReactNode;
    color: string;
    subtitle?: string;
    trend?: { value: number; isPositive: boolean };
  }) => (
    <motion.div
      whileHover={{ scale: 1.02 }}
      className={`bg-white p-6 rounded-xl shadow-lg border border-gray-100 ${color}`}
    >
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-gray-600 text-sm font-medium">{title}</p>
          <p className="text-3xl font-bold text-gray-900 mt-2">{value}</p>
          {subtitle && <p className="text-gray-500 text-sm mt-1">{subtitle}</p>}
          {trend && (
            <div className={`flex items-center mt-2 ${trend.isPositive ? 'text-green-600' : 'text-red-600'}`}>
              <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2}
                  d={trend.isPositive ? "M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" : "M13 17h8m0 0V9m0 8l-8-8-4 4-6-6"} />
              </svg>
              <span className="text-sm font-medium">{Math.abs(trend.value)}%</span>
            </div>
          )}
        </div>
        <div className={`p-3 rounded-lg ${color.split(' ')[0]}`}>
          {icon}
        </div>
      </div>
    </motion.div>
  );

  // Navigation tabs configuration
  const tabs = useMemo(() => {
    return [
      {
        id: 'dashboard',
        label: 'Dashboard',
        icon: 'M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z',
        color: 'text-blue-600'
      },
      {
        id: 'students',
        label: 'Students',
        icon: 'M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197M13 7a4 4 0 11-8 0 4 4 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z',
        color: 'text-green-600'
      },
      {
        id: 'instructors',
        label: 'Instructors',
        icon: 'M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z',
