          <Bar data={gpaDistributionData} options={{
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                display: false,
              }
            },
            scales: {
              y: {
                beginAtZero: true,
              }
            }
          }} />
        </div>
      </div>
    </motion.div>
  );

  // Main render
  return (
    <div className="flex min-h-screen bg-gray-50">
      {renderSidebar()}
      <div className="flex-1">
        <AnimatePresence>
          {activeTab === 'dashboard' && renderDashboard()}
          {activeTab === 'students' && <StudentManagement activeTab={activeTab} />}
          {activeTab === 'instructors' && <TeacherManagement activeTab={activeTab} />}
          {activeTab === 'departments' && <DepartmentManagement activeTab={activeTab} />}
          {activeTab === 'courses' && <CourseManagement activeTab={activeTab} />}
          {activeTab === 'analytics' && <div className="p-6">Analytics content coming soon...</div>}
          {activeTab === 'reports' && <div className="p-6">Reports content coming soon...</div>}
          {activeTab === 'messaging' && <MessagingSystem />}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default AdminDashboard;
